            color: var(--color-text-muted);
        }

        .establishment-tags {
            display: flex;
            gap: var(--space-xs);
            flex-wrap: wrap;
            margin-bottom: var(--space-md);
        }

        .tag {
            padding: var(--space-2xs) var(--space-xs);
            background-color: var(--color-bg-subtle);
            color: var(--color-text-secondary);
            font-size: var(--font-size-caption);
            border-radius: var(--radius-sm);
        }

        .establishment-actions {
            display: flex;
            gap: var(--space-sm);
            margin-top: auto;
        }

        .establishment-actions .btn {
            flex: 1;
        }

        /* Map View Toggle */
        .view-toggle {
            display: flex;
            gap: var(--space-xs);
            background-color: var(--color-bg-subtle);
            padding: var(--space-2xs);
            border-radius: var(--radius-md);
        }

        .view-toggle-btn {
            padding: var(--space-xs) var(--space-sm);
            border: none;
            background-color: transparent;
            font-size: var(--font-size-body-sm);
            font-weight: var(--font-weight-semibold);
            color: var(--color-text-secondary);
            cursor: pointer;
            border-radius: var(--radius-sm);
            transition: all var(--duration-normal) var(--easing-default);
            min-height: 36px;
        }

        .view-toggle-btn:hover {
            color: var(--color-text-default);
        }

        .view-toggle-btn.active {
            background-color: var(--color-bg-surface);
            color: var(--color-text-brand);
            box-shadow: var(--shadow-sm);
        }

        /* Map Placeholder */
        .map-container {
            background-color: var(--color-bg-surface);
            border: 1px solid var(--color-border-default);
            border-radius: var(--radius-xl);
            height: 500px;
            display: none;
            position: relative;
            overflow: hidden;
        }

        .map-container.active {
            display: block;
        }

        .map-placeholder {
            width: 100%;
            height: 100%;
            background-color: var(--color-bg-subtle);
            display: flex;
            align-items: center;
            justify-content: center;
            color: var(--color-text-muted);
            font-size: var(--font-size-body-lg);
        }

        .map-marker {
            position: absolute;
            width: 40px;
            height: 40px;
            background-color: var(--color-bg-brand);
            border: 3px solid var(--color-bg-surface);
            border-radius: 50%;
            cursor: pointer;
            box-shadow: var(--shadow-md);
            transition: transform var(--duration-normal) var(--easing-default);
        }

        .map-marker:hover {
            transform: scale(1.2);
            z-index: 10;
        }

        /* Story Section */
        .story-section {
            background-color: var(--color-bg-brand-subtle);
            padding: var(--space-3xl) 0;
            margin-top: var(--space-3xl);
        }

        .story-grid {
            display: grid;
            grid-template-columns: 1fr 1fr;
            gap: var(--space-3xl);
            align-items: center;
        }

        .story-content {
            max-width: 540px;
        }

        .story-badge {
            display: inline-block;
            padding: var(--space-xs) var(--space-sm);
            background-color: var(--color-bg-accent-magenta-subtle);
            color: var(--color-bg-accent-magenta);
            font-size: var(--font-size-caption);
            font-weight: var(--font-weight-semibold);
            border-radius: var(--radius-full);
            margin-bottom: var(--space-md);
        }

        .story-title {
            font-size: var(--font-size-h2);
            margin-bottom: var(--space-lg);
            line-height: var(--line-height-tight);
        }

        .story-text {
            font-size: var(--font-size-body-lg);
            color: var(--color-text-secondary);
            margin-bottom: var(--space-xl);
            line-height: var(--line-height-body);
        }

        .story-quote {
            font-size: var(--font-size-h4);
            font-style: italic;
            color: var(--color-text-brand);
            border-left: 4px solid var(--color-bg-accent-magenta);
            padding-left: var(--space-md);
            margin-bottom: var(--space-lg);
        }

        .story-author {
            display: flex;
            align-items: center;
            gap: var(--space-sm);
        }

        .story-author-avatar {
            width: 48px;
            height: 48px;
            border-radius: 50%;
            background-color: var(--color-bg-muted);
            object-fit: cover;
        }

        .story-author-info {
            display: flex;
            flex-direction: column;
        }

        .story-author-name {
            font-weight: var(--font-weight-semibold);
            color: var(--color-text-default);
        }

        .story-author-role {
            font-size: var(--font-size-caption);
            color: var(--color-text-muted);
        }

        .story-media {
            border-radius: var(--radius-xl);
            overflow: hidden;
            box-shadow: var(--shadow-lg);
        }

        .story-media img {
            width: 100%;
            height: auto;
            border-radius: 0;
        }

        /* CTA Section */
        .cta-section {
            background-color: var(--color-bg-brand);
            color: var(--color-text-inverse);
            border-radius: var(--radius-2xl);
            padding: var(--space-3xl);
            text-align: center;
            margin: var(--space-3xl) auto;
            max-width: 1000px;
        }

        .cta-section h2 {
            color: var(--color-text-inverse);
            margin-bottom: var(--space-sm);
        }

        .cta-section p {
            opacity: 0.9;
            margin-bottom: var(--space-xl);
            max-width: 600px;
            margin-left: auto;
            margin-right: auto;
        }

        .cta-buttons {
            display: flex;
            gap: var(--space-md);
            justify-content: center;
            flex-wrap: wrap;
        }

        .btn-white {
            background-color: var(--color-text-inverse);
            color: var(--color-bg-brand);
        }

        .btn-white:hover {
            background-color: #f0f0f0;
            color: var(--color-bg-brand);
        }

        .btn-outline-white {
            background-color: transparent;
            color: var(--color-text-inverse);
            border-color: var(--color-text-inverse);
        }

        .btn-outline-white:hover {
            background-color: rgba(255, 255, 255, 0.1);
            color: var(--color-text-inverse);
        }

        /* Footer */
        .footer {
            background-color: var(--color-bg-surface);
            border-top: 1px solid var(--color-border-default);
            padding: var(--space-3xl) 0;
            margin-top: var(--space-3xl);
        }

        .footer-grid {
            display: grid;
            grid-template-columns: repeat(4, 1fr);
            gap: var(--space-xl);
        }

        .footer-col h4 {
            font-size: var(--font-size-body);
            margin-bottom: var(--space-lg);
            color: var(--color-text-brand);
        }

        .footer-col ul {
            list-style: none;
        }

        .footer-col li {
            margin-bottom: var(--space-sm);
        }

        .footer-col a {
            color: var(--color-text-secondary);
            font-size: var(--font-size-body-sm);
        }

        .footer-col a:hover {
            color: var(--color-text-brand);
            text-decoration: underline;
        }

        /* Pagination */
        .pagination {
            display: flex;
            justify-content: center;
            align-items: center;
            gap: var(--space-xs);
            margin-top: var(--space-3xl);
        }

        .pagination-btn {
            width: 44px;
            height: 44px;
            display: flex;
            align-items: center;
            justify-content: center;
            border: 1px solid var(--color-border-default);
            border-radius: var(--radius-md);
            background-color: var(--color-bg-surface);
            font-size: var(--font-size-body);
            font-weight: var(--font-weight-semibold);
            color: var(--color-text-default);
            cursor: pointer;
            transition: all var(--duration-normal) var(--easing-default);
        }

        .pagination-btn:hover {
            border-color: var(--color-border-brand);
            color: var(--color-text-brand);
            background-color: var(--color-bg-brand-subtle);
        }

        .pagination-btn.active {
            background-color: var(--color-bg-brand);
            border-color: var(--color-bg-brand);
            color: var(--color-text-inverse);
        }

        .pagination-btn:disabled {
            opacity: 0.5;
            cursor: not-allowed;
        }

        /* -------------------------------------------------------------------------- */
        /* RESPONSIVE                                                                 */
        /* -------------------------------------------------------------------------- */
        @media (max-width: 1024px) {
            .establishments-grid {
                grid-template-columns: repeat(auto-fill, minmax(320px, 1fr));
            }

            .story-grid {
                grid-template-columns: 1fr;
                text-align: center;
            }

            .story-content {
                margin: 0 auto;
            }

            .story-quote {
                border-left: none;
                border-top: 4px solid var(--color-bg-accent-magenta);
                padding-left: 0;
                padding-top: var(--space-md);
            }

            .nav-links {
                display: none;
            }

            .footer-grid {
                grid-template-columns: 1fr 1fr;
            }
        }

        @media (max-width: 640px) {
            :root {
                --font-size-h1: 2rem;
                --space-3xl: 2rem;
            }

            .establishments-grid {
                grid-template-columns: 1fr;
            }

            .filter-container {
                flex-direction: column;
                align-items: stretch;
            }

            .search-input {
                width: 100%;
            }

            .results-count {
                margin-left: 0;
                margin-top: var(--space-sm);
            }

            .footer-grid {
                grid-template-columns: 1fr;
            }

            .header-actions .btn-outline {
                display: none;
            }

            .cta-buttons {
                flex-direction: column;
            }

            .cta-buttons .btn {
                width: 100%;
            }
        }
    </style>
</head>
