    <!-- Simple JavaScript for Interactions -->
    <script>
        // Map Toggle
        const mapToggle = document.getElementById('mapToggle');
        const mapView = document.getElementById('map');
        const gridView = document.getElementById('etablissements');
        const viewButtons = document.querySelectorAll('.view-toggle-btn');

        mapToggle.addEventListener('click', () => {
            const isActive = mapToggle.classList.contains('active');

            viewButtons.forEach(btn => {
                btn.classList.toggle('active');
                btn.setAttribute('aria-pressed', btn.classList.contains('active'));
            });

            if (isActive) {
                mapView.style.display = 'none';
                gridView.closest('.establishments-section').style.display = 'block';
            } else {
                mapView.style.display = 'block';
                gridView.closest('.establishments-section').style.display = 'none';
            }
        });

        // Filter Chip Removal
        document.querySelectorAll('.filter-chip .remove').forEach(removeBtn => {
            removeBtn.addEventListener('click', (e) => {
                e.stopPropagation();
                removeBtn.closest('.filter-chip').remove();
            });
        });

        // Pagination Active State
        document.querySelectorAll('.pagination-btn:not([disabled])').forEach(btn => {
            btn.addEventListener('click', () => {
                document.querySelectorAll('.pagination-btn').forEach(b => b.classList.remove('active'));
                btn.classList.add('active');
            });
        });
    </script>
