            --color-bg-brand-muted: #cce1ef;
            --color-bg-accent-green: #76b82a;
            --color-bg-accent-green-light: #c5d97f;
            --color-bg-accent-green-subtle: #f0f7e8;
            --color-bg-accent-orange: #ff841f;
            --color-bg-accent-orange-subtle: #fff5eb;
            --color-bg-accent-magenta: #cc209c;
            --color-bg-accent-magenta-subtle: #fce8f6;
            --color-bg-accent-cyan: #01c8d8;
            --color-bg-accent-cyan-subtle: #e6fafb;
            --color-bg-accent-yellow: #ffc52f;
            --color-bg-accent-yellow-subtle: #fffaeb;
            --color-bg-success: #76b82a;
            --color-bg-success-subtle: #f0f7e8;
            --color-bg-warning: #ff841f;
            --color-bg-warning-subtle: #fff5eb;
            --color-bg-error: #ef4444;
            --color-bg-error-subtle: #fef2f2;
            --color-bg-info: #01c8d8;
            --color-bg-info-subtle: #e6fafb;

            --color-text-default: #111111;
            --color-text-secondary: #636363;
            --color-text-muted: #a4a4a4;
            --color-text-inverse: #ffffff;
            --color-text-brand: #00619b;
            --color-text-brand-hover: #00517f;
            --color-text-success: #5f9622;
            --color-text-warning: #cc6a19;
            --color-text-error: #dc2626;
            --color-text-info: #01a0ad;

            --color-border-default: #e5e5e5;
            --color-border-subtle: #f3f3f3;
            --color-border-strong: #d4d4d4;
            --color-border-muted: #a4a4a4;
            --color-border-focus: #00619b;
            --color-border-brand: #00619b;
            --color-border-brand-subtle: #99c3df;

            --color-interactive: #00619b;
            --color-interactive-hover: #00517f;
            --color-interactive-active: #004063;

            /* Typography - From tokens.json semantic.font */
            --font-family: 'Aptos', -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
            --font-size-caption-sm: 0.625rem;
            --font-size-caption: 0.75rem;
            --font-size-body-sm: 0.875rem;
            --font-size-body: 1rem;
            --font-size-body-lg: 1.125rem;
            --font-size-body-md: 1.125rem;
            --font-size-button-xl: 1.5rem;
            --font-size-h6: 0.875rem;
            --font-size-h5: 1rem;
