                    year: "2015",
                    coords: { lat: 48.7875, lng: 2.3933 }
                }
                // Add more items as needed (28 total in production)
            ];

            // ----------------------------------------------------------------------
            // STATE MANAGEMENT
            // ----------------------------------------------------------------------
            const state = {
                filters: {
                    type: 'all', // 'all', 'etablissement', 'dispositif'
                    pole: '',
                    location: '',
                    age: '',
                    search: ''
                },
                activeChips: [],
                currentPage: 1,
                itemsPerPage: 6,
                view: 'grid', // 'grid' or 'map'
                sortBy: 'relevance'
            };

            // ----------------------------------------------------------------------
            // DOM ELEMENTS
            // ----------------------------------------------------------------------
            const elements = {
                // Filters
                typeTabs: document.querySelectorAll('.type-toggle-btn'),
                searchInput: document.getElementById('search'),
                poleSelect: document.getElementById('pole'),
                locationSelect: document.getElementById('location'),
                ageSelect: document.getElementById('age'),
                viewButtons: document.querySelectorAll('.view-toggle-btn'),
                clearFiltersBtn: document.getElementById('clear-filters'),
                activeFiltersContainer: document.getElementById('active-filters'),

                // Results
                gridView: document.getElementById('grid-view'),
                mapView: document.getElementById('map-view'),
                resultsCount: document.getElementById('results-count'),
                resultsTitle: document.getElementById('results-title'),
                emptyState: document.getElementById('empty-state'),
                loading: document.getElementById('loading'),
                pagination: document.getElementById('pagination'),
                sortSelect: document.getElementById('sort'),

                // Pagination
                prevPageBtn: document.getElementById('prev-page'),
                nextPageBtn: document.getElementById('next-page'),

                // Empty state reset
                resetFromEmpty: document.getElementById('reset-from-empty')
            };

            // ----------------------------------------------------------------------
            // UTILITY FUNCTIONS
            // ----------------------------------------------------------------------

            // Debounce function for search input
            function debounce(func, wait) {
                let timeout;
                return function executedFunction(...args) {
                    const later = () => {
                        clearTimeout(timeout);
                        func(...args);
                    };
                    clearTimeout(timeout);
                    timeout = setTimeout(later, wait);
                };
            }

            // Escape HTML to prevent XSS
            function escapeHtml(text) {
                const div = document.createElement('div');
                div.textContent = text;
                return div.innerHTML;
            }

            // Format pole label for display
            function formatPoleLabel(pole) {
                const labels = {
                    'education': 'Éducation',
                    'medicalise': 'Médicalisé',
                    'habitat': 'Habitat',
                    'professionnelle': 'Professionnel',
                    'protection': 'Protection'
                };
                return labels[pole] || pole;
            }

            // ----------------------------------------------------------------------
            // FILTERING LOGIC
            // ----------------------------------------------------------------------

            function applyFilters() {
                // Show loading state
                elements.gridView.innerHTML = '';
                elements.loading.classList.remove('hidden');
                elements.gridView.classList.add('hidden');
                elements.mapView.classList.add('hidden');
                elements.pagination.classList.add('hidden');
                elements.emptyState.classList.add('hidden');

                // Simulate API delay for realistic UX
                setTimeout(() => {
                    let filtered = [...data];

                    // Apply type filter
                    if (state.filters.type !== 'all') {
                        filtered = filtered.filter(item => item.type === state.filters.type);
                    }

                    // Apply pole filter
                    if (state.filters.pole) {
                        filtered = filtered.filter(item => item.pole === state.filters.pole);
                    }

                    // Apply location filter
                    if (state.filters.location) {
                        filtered = filtered.filter(item => item.locationSlug === state.filters.location);
                    }

                    // Apply age filter
                    if (state.filters.age) {
                        filtered = filtered.filter(item => item.ageGroup.includes(state.filters.age));
                    }

                    // Apply search filter
                    if (state.filters.search) {
                        const searchLower = state.filters.search.toLowerCase();
                        filtered = filtered.filter(item => 
                            item.name.toLowerCase().includes(searchLower) ||
                            item.location.toLowerCase().includes(searchLower) ||
                            item.description.toLowerCase().includes(searchLower) ||
                            item.tags.some(tag => tag.toLowerCase().includes(searchLower))
                        );
                    }

                    // Apply sorting
                    filtered = sortResults(filtered, state.sortBy);

                    // Update UI
                    renderResults(filtered);
                    updateResultsCount(filtered.length);
                    updateActiveChips();
                    toggleClearFiltersButton();

                    // Hide loading
                    elements.loading.classList.add('hidden');
                }, 300);
            }

            function sortResults(results, sortBy) {
                switch(sortBy) {
                    case 'name-asc':
                        return results.sort((a, b) => a.name.localeCompare(b.name));
                    case 'name-desc':
                        return results.sort((a, b) => b.name.localeCompare(a.name));
                    case 'location':
                        return results.sort((a, b) => a.location.localeCompare(b.location));
                    default: // relevance - keep original order
                        return results;
                }
            }

            function renderResults(results) {
                // Pagination
                const totalPages = Math.ceil(results.length / state.itemsPerPage);
                const startIndex = (state.currentPage - 1) * state.itemsPerPage;
                const paginatedResults = results.slice(startIndex, startIndex + state.itemsPerPage);

                if (paginatedResults.length === 0) {
                    elements.gridView.classList.add('hidden');
                    elements.emptyState.classList.remove('hidden');
                    elements.pagination.classList.add('hidden');
                    return;
                }

                elements.emptyState.classList.add('hidden');
                elements.gridView.classList.remove('hidden');

                // Render cards
                elements.gridView.innerHTML = paginatedResults.map(item => createResultCard(item)).join('');

                // Setup pagination
                if (totalPages > 1) {
                    renderPagination(totalPages);
                    elements.pagination.classList.remove('hidden');
                } else {
                    elements.pagination.classList.add('hidden');
                }

                // Setup map markers if in map view
                if (state.view === 'map') {
                    renderMapMarkers(results);
                }
            }

            function createResultCard(item) {
                const typeBadge = item.type === 'dispositif' ? 'badge-dispositif' : 'badge-type';
                const typeLabel = item.type === 'dispositif' ? 'Dispositif' : 'Établissement';

                return `
                    <article class="result-card" role="listitem" tabindex="0" aria-labelledby="result-title-${item.id}">
                        <div class="result-media">
                            <img src="${item.image}" alt="${escapeHtml(item.name)} - ${escapeHtml(item.location)}" loading="lazy">
                            <div class="result-badges">
                                <span class="badge badge-pole">${escapeHtml(formatPoleLabel(item.pole))}</span>
                                <span class="badge ${typeBadge}">${typeLabel}</span>
                            </div>
                        </div>
                        <div class="result-content">
                            <div class="result-location">
                                <svg width="16" height="16" viewBox="0 0 16 16" fill="none" xmlns="http://www.w3.org/2000/svg" aria-hidden="true">
                                    <path d="M8 1C5.5 1 3.5 3 3.5 5.5C3.5 8.5 8 14 8 14C8 14 12.5 8.5 12.5 5.5C12.5 3 10.5 1 8 1Z" stroke="#636363" stroke-width="1.5"/>
                                    <circle cx="8" cy="5.5" r="2" stroke="#636363" stroke-width="1.5"/>
                                </svg>
                                ${escapeHtml(item.location)}
                            </div>
                            <h3 class="result-title">
                                <a href="etablissement-detail.html?id=${item.id}" id="result-title-${item.id}">
                                    ${escapeHtml(item.name)}
                                </a>
                            </h3>
                            <p class="result-description">${escapeHtml(item.description)}</p>
                            <div class="result-meta">
                                <span class="meta-item">
                                    <svg width="14" height="14" viewBox="0 0 14 14" fill="none" xmlns="http://www.w3.org/2000/svg" aria-hidden="true">
                                        <path d="M7 1C4.5 1 2.5 3 2.5 5.5C2.5 8.5 7 13 7 13C7 13 11.5 8.5 11.5 5.5C11.5 3 9.5 1 7 1Z" stroke="#636363" stroke-width="1"/>
                                        <circle cx="7" cy="5.5" r="1.5" stroke="#636363" stroke-width="1"/>
                                    </svg>
                                    ${item.ageGroup.map(age => age === 'enfants' ? 'Enfants' : age === 'ados' ? 'Ados' : 'Adultes').join(', ')}
                                </span>
                                <span class="meta-item">
                                    <svg width="14" height="14" viewBox="0 0 14 14" fill="none" xmlns="http://www.w3.org/2000/svg" aria-hidden="true">
                                        <circle cx="7" cy="4" r="2" stroke="#636363" stroke-width="1"/>
                                        <path d="M2 12C2 9.23858 4.23858 7 7 7C9.76142 7 12 9.23858 12 12" stroke="#636363" stroke-width="1" stroke-linecap="round"/>
                                    </svg>
                                    ${item.capacity}
                                </span>
                            </div>
                            <div class="result-tags">
                                ${item.tags.slice(0, 3).map(tag => `<span class="tag">${escapeHtml(tag)}</span>`).join('')}
                                ${item.tags.length > 3 ? `<span class="tag">+${item.tags.length - 3}</span>` : ''}
                            </div>
                            <div class="result-actions">
                                <a href="etablissement-detail.html?id=${item.id}" class="btn btn-outline">Découvrir</a>
                                <a href="contact.html?etablissement=${item.id}" class="btn btn-primary">Contacter</a>
                            </div>
                        </div>
                    </article>
                `;
            }

            function renderPagination(totalPages) {
                let html = '';

                // Previous button
                html += `<button class="pagination-btn" id="prev-page" aria-label="Page précédente" ${state.currentPage === 1 ? 'disabled' : ''}>
                    <svg width="20" height="20" viewBox="0 0 20 20" fill="none" xmlns="http://www.w3.org/2000/svg" aria-hidden="true">
                        <path d="M12.5 15L7.5 10L12.5 5" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"/>
                    </svg>
                </button>`;

                // Page numbers
                const maxVisible = 5;
                let startPage = Math.max(1, state.currentPage - Math.floor(maxVisible / 2));
                let endPage = Math.min(totalPages, startPage + maxVisible - 1);

                if (endPage - startPage + 1 < maxVisible) {
                    startPage = Math.max(1, endPage - maxVisible + 1);
                }

                if (startPage > 1) {
                    html += `<button class="pagination-btn" data-page="1">1</button>`;
                    if (startPage > 2) {
                        html += `<span class="pagination-ellipsis">…</span>`;
                    }
                }

                for (let i = startPage; i <= endPage; i++) {
                    html += `<button class="pagination-btn ${i === state.currentPage ? 'active' : ''}" 
                        data-page="${i}" 
                        aria-current="${i === state.currentPage ? 'page' : 'false'}"
                        aria-label="Page ${i}">${i}</button>`;
                }

                if (endPage < totalPages) {
                    if (endPage < totalPages - 1) {
                        html += `<span class="pagination-ellipsis">…</span>`;
                    }
                    html += `<button class="pagination-btn" data-page="${totalPages}">${totalPages}</button>`;
                }

                // Next button
                html += `<button class="pagination-btn" id="next-page" aria-label="Page suivante" ${state.currentPage === totalPages ? 'disabled' : ''}>
                    <svg width="20" height="20" viewBox="0 0 20 20" fill="none" xmlns="http://www.w3.org/2000/svg" aria-hidden="true">
                        <path d="M7.5 5L12.5 10L7.5 15" stroke="currentColor" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"/>
                    </svg>
                </button>`;

                elements.pagination.innerHTML = html;
            }

            function renderMapMarkers(results) {
                // Clear existing markers
                document.querySelectorAll('.map-marker').forEach(marker => marker.remove());

                // Add markers for visible results (simplified for prototype)
                results.slice(0, 10).forEach((item, index) => {
                    const marker = document.createElement('button');
                    marker.className = 'map-marker';
                    marker.setAttribute('data-type', item.type);
                    marker.setAttribute('aria-label', `${item.name} - ${item.location}`);
                    marker.innerHTML = index + 1;
                    marker.style.top = `${30 + (index * 5)}%`;
                    marker.style.left = `${20 + (index * 7)}%`;
                    marker.addEventListener('click', () => {
                        // In production: open info window or navigate to detail page
                        alert(`${item.name}\n${item.location}\n${item.poleLabel}`);
                    });
                    elements.mapView.appendChild(marker);
                });
            }

            function updateResultsCount(count) {
                const typeLabel = state.filters.type === 'etablissement' ? 'établissement' : 
                                 state.filters.type === 'dispositif' ? 'dispositif' : 
                                 'établissement et dispositif';
                const plural = count > 1 ? 's' : '';
                elements.resultsCount.textContent = `${count} ${typeLabel}${plural}`;

                // Update page title based on filters
                if (state.filters.pole || state.filters.location || state.filters.search) {
                    elements.resultsTitle.textContent = 'Résultats de recherche';
                } else {
                    elements.resultsTitle.textContent = `Tous les ${typeLabel}${plural}`;
                }
            }

            function updateActiveChips() {
                const chips = [];

                if (state.filters.type !== 'all') {
                    chips.push({
                        key: 'type',
                        label: state.filters.type === 'etablissement' ? 'Établissements' : 'Dispositifs',
                        value: state.filters.type
                    });
                }

                if (state.filters.pole) {
                    chips.push({
                        key: 'pole',
                        label: formatPoleLabel(state.filters.pole),
                        value: state.filters.pole
                    });
                }

                if (state.filters.location) {
                    const locationLabels = {
                        'creteil': 'Créteil',
                        'saint-maur': 'Saint-Maur',
                        'vitry': 'Vitry-sur-Seine',
                        'champigny': 'Champigny-sur-Marne',
                        'ivry': 'Ivry-sur-Seine',
                        'bonneuil': 'Bonneuil-sur-Marne',
                        'le-perreux': 'Le Perreux',
                        'nogent': 'Nogent-sur-Marne'
                    };
                    chips.push({
                        key: 'location',
                        label: locationLabels[state.filters.location] || state.filters.location,
                        value: state.filters.location
                    });
                }

                if (state.filters.age) {
                    const ageLabels = {
                        'enfants': 'Enfants',
                        'ados': 'Adolescents',
                        'adultes': 'Adultes'
                    };
                    chips.push({
                        key: 'age',
                        label: ageLabels[state.filters.age],
                        value: state.filters.age
                    });
                }

                if (state.filters.search) {
                    chips.push({
                        key: 'search',
                        label: `"${state.filters.search}"`,
                        value: state.filters.search
                    });
                }

                state.activeChips = chips;

                if (chips.length > 0) {
                    elements.activeFiltersContainer.innerHTML = chips.map(chip => `
                        <button class="filter-chip active" data-key="${chip.key}" data-value="${chip.value}" aria-label="Supprimer le filtre ${chip.label}">
                            ${chip.label}
                            <span class="remove" aria-hidden="true">×</span>
                        </button>
                    `).join('');
                } else {
                    elements.activeFiltersContainer.innerHTML = '';
                }
            }

            function toggleClearFiltersButton() {
                const hasFilters = state.filters.type !== 'all' || 
                                  state.filters.pole || 
                                  state.filters.location || 
                                  state.filters.age || 
                                  state.filters.search;
                elements.clearFiltersBtn.hidden = !hasFilters;
            }

            function clearAllFilters() {
                state.filters = {
                    type: 'all',
                    pole: '',
                    location: '',
                    age: '',
                    search: ''
                };
                state.currentPage = 1;

                // Reset UI controls
                elements.typeTabs.forEach(tab => {
                    tab.classList.toggle('active', tab.dataset.type === 'all');
                    tab.setAttribute('aria-selected', tab.dataset.type === 'all');
                });
                elements.searchInput.value = '';
                elements.poleSelect.value = '';
                elements.locationSelect.value = '';
                elements.ageSelect.value = '';

                applyFilters();
            }

            // ----------------------------------------------------------------------
            // EVENT LISTENERS
            // ----------------------------------------------------------------------

            // Type toggle tabs
            elements.typeTabs.forEach(tab => {
                tab.addEventListener('click', () => {
                    // Update active state
                    elements.typeTabs.forEach(t => {
                        t.classList.remove('active');
                        t.setAttribute('aria-selected', 'false');
                    });
                    tab.classList.add('active');
                    tab.setAttribute('aria-selected', 'true');

                    // Update state and filter
                    state.filters.type = tab.dataset.type;
                    state.currentPage = 1;
                    applyFilters();
                });
            });

            // Search input with debounce
            const debouncedSearch = debounce((value) => {
                state.filters.search = value.trim();
                state.currentPage = 1;
                applyFilters();
            }, 300);

            elements.searchInput.addEventListener('input', (e) => {
                debouncedSearch(e.target.value);
            });

            // Filter selects
            elements.poleSelect.addEventListener('change', (e) => {
                state.filters.pole = e.target.value;
                state.currentPage = 1;
                applyFilters();
            });

            elements.locationSelect.addEventListener('change', (e) => {
                state.filters.location = e.target.value;
                state.currentPage = 1;
                applyFilters();
            });

            elements.ageSelect.addEventListener('change', (e) => {
                state.filters.age = e.target.value;
                state.currentPage = 1;
                applyFilters();
            });

            // View toggle
            elements.viewButtons.forEach(btn => {
                btn.addEventListener('click', () => {
                    const view = btn.dataset.view;

                    // Update active state
                    elements.viewButtons.forEach(b => {
                        b.classList.remove('active');
                        b.setAttribute('aria-selected', 'false');
                    });
                    btn.classList.add('active');
                    btn.setAttribute('aria-selected', 'true');

                    // Update view
                    state.view = view;

                    if (view === 'grid') {
                        elements.gridView.classList.remove('hidden');
                        elements.mapView.classList.add('hidden');
                        elements.mapView.hidden = true;
                    } else {
                        elements.gridView.classList.add('hidden');
                        elements.mapView.classList.remove('hidden');
                        elements.mapView.hidden = false;
                        // Re-render markers with current filtered results
                        const filtered = filterData();
                        renderMapMarkers(filtered);
                    }
                });
            });

            // Clear filters button
            elements.clearFiltersBtn.addEventListener('click', clearAllFilters);
            elements.resetFromEmpty.addEventListener('click', clearAllFilters);

            // Active filter chip removal
            elements.activeFiltersContainer.addEventListener('click', (e) => {
                if (e.target.classList.contains('remove') || e.target.closest('.remove')) {
                    const chip = e.target.closest('.filter-chip');
                    const key = chip.dataset.key;
                    const value = chip.dataset.value;

                    // Remove from state
                    if (key === 'search') {
                        state.filters.search = '';
                        elements.searchInput.value = '';
                    } else if (key === 'type') {
                        state.filters.type = 'all';
                        // Update tab UI
                        elements.typeTabs.forEach(tab => {
                            tab.classList.toggle('active', tab.dataset.type === 'all');
                            tab.setAttribute('aria-selected', tab.dataset.type === 'all');
                        });
                    } else {
                        state.filters[key] = '';
                        // Reset corresponding select
                        if (elements[`${key}Select`]) {
                            elements[`${key}Select`].value = '';
                        }
                    }

                    state.currentPage = 1;
                    applyFilters();
                }
            });

            // Pagination
            elements.pagination.addEventListener('click', (e) => {
                const btn = e.target.closest('.pagination-btn');
                if (!btn || btn.disabled) return;

                if (btn.id === 'prev-page' && state.currentPage > 1) {
                    state.currentPage--;
                } else if (btn.id === 'next-page') {
                    const totalPages = Math.ceil(filterData().length / state.itemsPerPage);
                    if (state.currentPage < totalPages) {
                        state.currentPage++;
                    }
                } else if (btn.dataset.page) {
                    state.currentPage = parseInt(btn.dataset.page);
                }

                // Re-render with new page
                const filtered = filterData();
                renderResults(filtered);

                // Scroll to results
                document.querySelector('.results-section').scrollIntoView({ behavior: 'smooth' });
            });

            // Sort select
            elements.sortSelect.addEventListener('change', (e) => {
                state.sortBy = e.target.value;
                state.currentPage = 1;
                applyFilters();
            });

            // Keyboard navigation for cards
            elements.gridView.addEventListener('keydown', (e) => {
                if (e.key === 'Enter' || e.key === ' ') {
                    const card = e.target.closest('.result-card');
                    if (card) {
                        const link = card.querySelector('a[href]');
                        if (link) {
                            e.preventDefault();
                            window.location.href = link.href;
                        }
                    }
                }
            });

            // ----------------------------------------------------------------------
            // HELPER: Filter data without rendering (for map view)
            // ----------------------------------------------------------------------
            function filterData() {
                let filtered = [...data];

                if (state.filters.type !== 'all') {
                    filtered = filtered.filter(item => item.type === state.filters.type);
                }
                if (state.filters.pole) {
                    filtered = filtered.filter(item => item.pole === state.filters.pole);
                }
                if (state.filters.location) {
                    filtered = filtered.filter(item => item.locationSlug === state.filters.location);
                }
                if (state.filters.age) {
                    filtered = filtered.filter(item => item.ageGroup.includes(state.filters.age));
                }
                if (state.filters.search) {
                    const searchLower = state.filters.search.toLowerCase();
                    filtered = filtered.filter(item => 
                        item.name.toLowerCase().includes(searchLower) ||
                        item.location.toLowerCase().includes(searchLower) ||
                        item.description.toLowerCase().includes(searchLower) ||
                        item.tags.some(tag => tag.toLowerCase().includes(searchLower))
                    );
                }

                return sortResults(filtered, state.sortBy);
            }

            // ----------------------------------------------------------------------
            // INITIALIZE
            // ----------------------------------------------------------------------

            // Initial render
            applyFilters();

            // Update URL with filters for shareability (optional)
            function updateURL() {
                const params = new URLSearchParams();
                if (state.filters.type !== 'all') params.set('type', state.filters.type);
                if (state.filters.pole) params.set('pole', state.filters.pole);
                if (state.filters.location) params.set('location', state.filters.location);
                if (state.filters.age) params.set('age', state.filters.age);
                if (state.filters.search) params.set('q', state.filters.search);

                const newURL = `${window.location.pathname}${params.toString() ? '?' + params.toString() : ''}`;
                history.replaceState(null, '', newURL);
            }

            // Load filters from URL on page load
            function loadFiltersFromURL() {
                const params = new URLSearchParams(window.location.search);

                if (params.get('type')) {
                    state.filters.type = params.get('type');
                    // Update tab UI
                    elements.typeTabs.forEach(tab => {
                        const isActive = tab.dataset.type === state.filters.type;
                        tab.classList.toggle('active', isActive);
                        tab.setAttribute('aria-selected', isActive);
                    });
                }
                if (params.get('pole')) {
                    state.filters.pole = params.get('pole');
                    elements.poleSelect.value = params.get('pole');
                }
                if (params.get('location')) {
                    state.filters.location = params.get('location');
                    elements.locationSelect.value = params.get('location');
                }
                if (params.get('age')) {
                    state.filters.age = params.get('age');
                    elements.ageSelect.value = params.get('age');
                }
                if (params.get('q')) {
                    state.filters.search = params.get('q');
                    elements.searchInput.value = params.get('q');
                }
            }

            // Initialize from URL if present
            if (window.location.search) {
                loadFiltersFromURL();
                applyFilters();
            }

            // Update URL when filters change (debounced)
            const debouncedURLUpdate = debounce(updateURL, 500);
            const originalApplyFilters = applyFilters;
            applyFilters = function() {
                originalApplyFilters();
                debouncedURLUpdate();
            };

            // Accessibility: Skip link focus management
            const skipLink = document.createElement('a');
            skipLink.href = '#main-content';
            skipLink.className = 'sr-only';
            skipLink.style.cssText = 'position:absolute;left:-9999px;top:auto;width:1px;height:1px;overflow:hidden;z-index:10000;';
            skipLink.textContent = 'Aller au contenu principal';
            skipLink.addEventListener('focus', () => {
                skipLink.style.cssText = 'position:fixed;left:var(--space-lg);top:var(--space-lg);width:auto;height:auto;padding:var(--space-sm) var(--space-md);background:var(--color-bg-brand);color:var(--color-text-inverse);border-radius:var(--radius-md);font-weight:var(--font-weight-semibold);z-index:var(--z-modal);';
            });
            skipLink.addEventListener('blur', () => {
                skipLink.style.cssText = 'position:absolute;left:-9999px;top:auto;width:1px;height:1px;overflow:hidden;z-index:10000;';
            });
            document.body.insertBefore(skipLink, document.body.firstChild);

            // Announce results to screen readers
            const resultsAnnouncer = document.createElement('div');
            resultsAnnouncer.setAttribute('aria-live', 'polite');
            resultsAnnouncer.setAttribute('aria-atomic', 'true');
            resultsAnnouncer.className = 'sr-only';
            document.body.appendChild(resultsAnnouncer);

            // Update announcer when results change
            const originalUpdateResultsCount = updateResultsCount;
            updateResultsCount = function(count) {
                originalUpdateResultsCount(count);
                const typeLabel = state.filters.type === 'etablissement' ? 'établissement' : 
                                 state.filters.type === 'dispositif' ? 'dispositif' : 
                                 'établissement et dispositif';
                resultsAnnouncer.textContent = `${count} ${typeLabel} trouvé${count > 1 ? 's' : ''}`;
            };

            console.log('✅ Apogei 94 Filtering System initialized');
            console.log('📊 Design tokens loaded from tokens.json & theme.json');
            console.log('♿ Accessibility features enabled');
        });
    </script>
</body>
</html>
