            font-size: var(--font-size-body-lg);
            color: var(--color-text-secondary);
            margin-bottom: var(--space-lg);
        }

        .detail-hero-description {
            font-size: var(--font-size-body-lg);
            color: var(--color-text-secondary);
            margin-bottom: var(--space-xl);
            line-height: var(--line-height-body);
        }

        .detail-hero-actions {
            display: flex;
            gap: var(--space-md);
            flex-wrap: wrap;
        }

        .detail-hero-media {
            position: relative;
            border-radius: var(--radius-xl);
            overflow: hidden;
            box-shadow: var(--shadow-lg);
            aspect-ratio: 4/3;
        }

        .detail-hero-media img {
            width: 100%;
            height: 100%;
            object-fit: cover;
            border-radius: var(--radius-xl);
        }

        .media-gallery-trigger {
            position: absolute;
            bottom: var(--space-md);
            right: var(--space-md);
            background-color: var(--color-bg-surface);
            padding: var(--space-xs) var(--space-sm);
            border-radius: var(--radius-md);
            font-size: var(--font-size-body-sm);
            font-weight: var(--font-weight-semibold);
            display: flex;
            align-items: center;
            gap: var(--space-2xs);
            cursor: pointer;
            box-shadow: var(--shadow-md);
        }

        /* Info Cards Grid */
        .info-section {
            padding: var(--space-3xl) 0;
        }

        .info-grid {
            display: grid;
            grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
            gap: var(--space-lg);
            margin-bottom: var(--space-3xl);
        }

        .info-card {
            background-color: var(--color-bg-surface);
            border: 1px solid var(--color-border-default);
            border-radius: var(--radius-xl);
            padding: var(--space-lg);
            transition: all var(--duration-slow) var(--easing-default);
        }

        .info-card:hover {
            border-color: var(--color-border-brand);
            box-shadow: var(--shadow-md);
        }

        .info-card-icon {
            width: 48px;
            height: 48px;
            background-color: var(--color-bg-brand-subtle);
            border-radius: var(--radius-lg);
            display: flex;
            align-items: center;
            justify-content: center;
            margin-bottom: var(--space-md);
            color: var(--color-text-brand);
        }

        .info-card-title {
            font-size: var(--font-size-h4);
            margin-bottom: var(--space-sm);
        }

        .info-card-content {
            font-size: var(--font-size-body-sm);
            color: var(--color-text-secondary);
            line-height: var(--line-height-body);
        }

        .info-card-list {
            list-style: none;
            margin-top: var(--space-sm);
        }

        .info-card-list li {
            display: flex;
            align-items: center;
            gap: var(--space-xs);
            padding: var(--space-2xs) 0;
            font-size: var(--font-size-body-sm);
            color: var(--color-text-secondary);
        }

        .info-card-list li svg {
            flex-shrink: 0;
            color: var(--color-text-brand);
        }

        /* Content Sections */
        .content-section {
            background-color: var(--color-bg-surface);
            border: 1px solid var(--color-border-default);
            border-radius: var(--radius-xl);
            padding: var(--space-xl);
            margin-bottom: var(--space-lg);
        }

        .content-section-title {
            font-size: var(--font-size-h2);
            margin-bottom: var(--space-lg);
            padding-bottom: var(--space-md);
            border-bottom: 1px solid var(--color-border-subtle);
        }

        .content-section-text {
            font-size: var(--font-size-body);
            color: var(--color-text-secondary);
            line-height: var(--line-height-body);
            margin-bottom: var(--space-md);
        }

        .content-section-text:last-child {
            margin-bottom: 0;
        }

        /* Services Grid */
        .services-grid {
            display: grid;
            grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
            gap: var(--space-lg);
            margin-top: var(--space-lg);
        }

        .service-card {
            background-color: var(--color-bg-subtle);
            border-radius: var(--radius-lg);
            padding: var(--space-lg);
            transition: all var(--duration-normal) var(--easing-default);
        }

        .service-card:hover {
            background-color: var(--color-bg-brand-subtle);
        }

        .service-card-icon {
            width: 40px;
            height: 40px;
            background-color: var(--color-bg-brand);
            border-radius: var(--radius-md);
            display: flex;
            align-items: center;
            justify-content: center;
            margin-bottom: var(--space-md);
            color: var(--color-text-inverse);
        }

        .service-card-title {
            font-size: var(--font-size-h4);
            margin-bottom: var(--space-sm);
            color: var(--color-text-default);
        }

        .service-card-description {
            font-size: var(--font-size-body-sm);
            color: var(--color-text-secondary);
            line-height: var(--line-height-body);
        }

        /* Team Section */
        .team-grid {
            display: grid;
            grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
            gap: var(--space-lg);
            margin-top: var(--space-lg);
        }

        .team-card {
            text-align: center;
            padding: var(--space-lg);
            background-color: var(--color-bg-subtle);
            border-radius: var(--radius-lg);
        }

        .team-avatar {
            width: 80px;
            height: 80px;
            border-radius: 50%;
            background-color: var(--color-bg-muted);
            margin: 0 auto var(--space-md);
            object-fit: cover;
        }

        .team-name {
            font-size: var(--font-size-h4);
            margin-bottom: var(--space-2xs);
        }

        .team-role {
            font-size: var(--font-size-body-sm);
            color: var(--color-text-secondary);
            margin-bottom: var(--space-xs);
        }

        .team-contact {
            font-size: var(--font-size-body-sm);
            color: var(--color-text-brand);
        }

        /* Map Section */
        .map-section {
            background-color: var(--color-bg-surface);
            border: 1px solid var(--color-border-default);
            border-radius: var(--radius-xl);
            overflow: hidden;
            margin-bottom: var(--space-lg);
        }

        .map-container {
            height: 400px;
            background: linear-gradient(135deg, var(--color-bg-brand-subtle) 0%, var(--color-bg-accent-cyan-subtle) 100%);
            display: flex;
            align-items: center;
            justify-content: center;
            color: var(--color-text-brand);
            font-size: var(--font-size-body-lg);
            flex-direction: column;
            gap: var(--space-md);
            text-align: center;
            padding: var(--space-lg);
        }

        .map-address {
            padding: var(--space-lg);
            background-color: var(--color-bg-subtle);
            display: flex;
            align-items: center;
            gap: var(--space-md);
            font-size: var(--font-size-body);
            color: var(--color-text-secondary);
        }

        /* Contact Form */
        .contact-form {
            display: grid;
            grid-template-columns: 1fr 1fr;
            gap: var(--space-md);
            margin-top: var(--space-lg);
        }

        .form-group {
            display: flex;
            flex-direction: column;
            gap: var(--space-2xs);
        }

        .form-group.full-width {
            grid-column: 1 / -1;
        }

        .form-label {
            font-size: var(--font-size-body-sm);
            font-weight: var(--font-weight-semibold);
            color: var(--color-text-default);
        }

        .form-input,
        .form-textarea,
        .form-select {
            padding: var(--space-xs) var(--space-sm);
            border: 1px solid var(--color-border-strong);
            border-radius: var(--radius-md);
            font-size: var(--font-size-body-sm);
            font-family: var(--font-family);
            min-height: 44px;
            background-color: var(--color-bg-surface);
            transition: border-color var(--duration-normal) var(--easing-default);
        }

        .form-input:focus,
        .form-textarea:focus,
        .form-select:focus {
            border-color: var(--color-border-focus);
            outline: none;
            box-shadow: var(--shadow-focus);
        }

        .form-textarea {
            min-height: 120px;
            resize: vertical;
        }

        .form-help {
            font-size: var(--font-size-caption);
            color: var(--color-text-muted);
        }

        /* Related Establishments */
        .related-grid {
            display: grid;
            grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
            gap: var(--space-lg);
            margin-top: var(--space-lg);
        }

        .related-card {
            background-color: var(--color-bg-surface);
            border: 1px solid var(--color-border-default);
            border-radius: var(--radius-lg);
            overflow: hidden;
            transition: all var(--duration-slow) var(--easing-default);
        }

        .related-card:hover {
            transform: translateY(-4px);
            box-shadow: var(--shadow-md);
            border-color: var(--color-border-brand);
        }

        .related-media {
            height: 160px;
            background-color: var(--color-bg-muted);
            overflow: hidden;
        }

        .related-media img {
            width: 100%;
            height: 100%;
            object-fit: cover;
            border-radius: 0;
        }

        .related-content {
            padding: var(--space-md);
        }

        .related-location {
            display: flex;
            align-items: center;
            gap: var(--space-2xs);
            font-size: var(--font-size-body-sm);
            color: var(--color-text-secondary);
            margin-bottom: var(--space-2xs);
        }

        .related-title {
            font-size: var(--font-size-h4);
            margin-bottom: var(--space-xs);
        }

        .related-title a {
            color: var(--color-text-default);
        }

        .related-title a:hover {
            color: var(--color-text-brand);
        }

        .related-type {
            display: inline-block;
            padding: var(--space-2xs) var(--space-xs);
            background-color: var(--color-bg-brand-subtle);
            color: var(--color-text-brand);
            font-size: var(--font-size-caption);
            font-weight: var(--font-weight-semibold);
            border-radius: var(--radius-full);
            margin-bottom: var(--space-sm);
        }

        /* CTA Section */
        .cta-section {
            background-color: var(--color-bg-brand);
            color: var(--color-text-inverse);
            border-radius: var(--radius-xl);
            padding: var(--space-3xl);
            text-align: center;
            margin: var(--space-3xl) auto;
            max-width: 1000px;
        }

        .cta-section h2 {
            color: var(--color-text-inverse);
            margin-bottom: var(--space-sm);
        }

        .cta-section p {
            opacity: 0.9;
            margin-bottom: var(--space-xl);
            max-width: 600px;
            margin-left: auto;
            margin-right: auto;
        }

        .cta-buttons {
            display: flex;
            gap: var(--space-md);
            justify-content: center;
            flex-wrap: wrap;
        }

        .btn-white {
            background-color: var(--color-text-inverse);
            color: var(--color-bg-brand);
        }

        .btn-white:hover {
            background-color: #f0f0f0;
            color: var(--color-bg-brand);
        }

        .btn-outline-white {
            background-color: transparent;
            color: var(--color-text-inverse);
            border-color: var(--color-text-inverse);
        }

        .btn-outline-white:hover {
            background-color: rgba(255, 255, 255, 0.1);
            color: var(--color-text-inverse);
        }

        /* Footer */
        .footer {
            background-color: var(--color-bg-surface);
            border-top: 1px solid var(--color-border-default);
            padding: var(--space-3xl) 0;
            margin-top: var(--space-3xl);
        }

        .footer-grid {
            display: grid;
            grid-template-columns: repeat(4, 1fr);
            gap: var(--space-xl);
        }

        .footer-col h4 {
            font-size: var(--font-size-body);
            margin-bottom: var(--space-lg);
            color: var(--color-text-brand);
        }

        .footer-col ul {
            list-style: none;
        }

        .footer-col li {
            margin-bottom: var(--space-sm);
        }

        .footer-col a {
            color: var(--color-text-secondary);
            font-size: var(--font-size-body-sm);
        }

        .footer-col a:hover {
            color: var(--color-text-brand);
            text-decoration: underline;
        }

        /* Back to Results Link */
        .back-to-results {
            display: inline-flex;
            align-items: center;
            gap: var(--space-xs);
            font-size: var(--font-size-body-sm);
            font-weight: var(--font-weight-semibold);
            color: var(--color-text-brand);
            margin-bottom: var(--space-lg);
            padding: var(--space-2xs) var(--space-xs);
            border-radius: var(--radius-md);
            transition: all var(--duration-normal) var(--easing-default);
        }

        .back-to-results:hover {
            background-color: var(--color-bg-brand-subtle);
            text-decoration: none;
        }

        /* Accessibility: Reduced Motion */
        @media (prefers-reduced-motion: reduce) {
            *,
            *::before,
            *::after {
                animation-duration: 0.01ms !important;
                animation-iteration-count: 1 !important;
                transition-duration: 0.01ms !important;
                scroll-behavior: auto !important;
            }
        }

        /* -------------------------------------------------------------------------- */
        /* RESPONSIVE                                                                 */
        /* -------------------------------------------------------------------------- */
        @media (max-width: 1024px) {
            .detail-hero-grid {
                grid-template-columns: 1fr;
            }

            .detail-hero-media {
                order: -1;
            }

            .footer-grid {
                grid-template-columns: 1fr 1fr;
            }

            .nav-links {
                display: none;
            }

            .contact-form {
                grid-template-columns: 1fr;
            }
        }

        @media (max-width: 768px) {
            :root {
                --font-size-h1: 2rem;
                --space-3xl: 2rem;
            }

            .detail-hero-title {
                font-size: var(--font-size-h1);
            }

            .detail-hero-actions {
                flex-direction: column;
            }

            .detail-hero-actions .btn {
                width: 100%;
            }

            .footer-grid {
                grid-template-columns: 1fr;
            }

            .header-actions .btn-outline {
                display: none;
            }

            .cta-buttons {
                flex-direction: column;
            }

            .cta-buttons .btn {
                width: 100%;
            }

            .info-grid {
                grid-template-columns: 1fr;
            }
        }

        @media (max-width: 480px) {
            .detail-hero-badges {
                flex-direction: column;
                align-items: flex-start;
            }

            .back-to-results {
                width: 100%;
                justify-content: center;
            }
        }
    </style>
</head>
