            // STATE MANAGEMENT
            // ----------------------------------------------------------------------
            const state = {
                filters: {
                    search: '',
                    type: '',
                    location: '',
                    metier: ''
                },
                activeChips: [],
                sortBy: 'relevance'
            };

            // ----------------------------------------------------------------------
            // DOM ELEMENTS
            // ----------------------------------------------------------------------
            const elements = {
                // Filters
                searchInput: document.getElementById('job-search'),
                typeSelect: document.getElementById('job-type'),
                locationSelect: document.getElementById('job-location'),
                metierSelect: document.getElementById('job-metier'),
                clearFiltersBtn: document.getElementById('clear-filters'),
                activeFiltersContainer: document.getElementById('active-filters'),

                // Results
                jobsGrid: document.getElementById('jobs-grid'),
                jobsCount: document.getElementById('jobs-count'),
                emptyState: document.getElementById('empty-state'),
                loading: document.getElementById('loading'),

                // Empty state reset
                resetFromEmpty: document.getElementById('reset-from-empty')
            };

            // ----------------------------------------------------------------------
            // UTILITY FUNCTIONS
            // ----------------------------------------------------------------------

            // Debounce function for search input
            function debounce(func, wait) {
                let timeout;
                return function executedFunction(...args) {
                    const later = () => {
                        clearTimeout(timeout);
                        func(...args);
                    };
                    clearTimeout(timeout);
                    timeout = setTimeout(later, wait);
                };
            }

            // Escape HTML to prevent XSS
            function escapeHtml(text) {
                const div = document.createElement('div');
                div.textContent = text;
                return div.innerHTML;
            }

            // Format type label for display
            function formatTypeLabel(type) {
                const labels = {
                    'cdi': 'CDI',
                    'cdd': 'CDD',
                    'interim': 'Intérim',
                    'stage': 'Stage'
                };
                return labels[type] || type;
            }

            // Format location label for display
            function formatLocationLabel(location) {
                const labels = {
                    'creteil': 'Créteil',
                    'saint-maur': 'Saint-Maur',
                    'vitry': 'Vitry-sur-Seine',
                    'champigny': 'Champigny-sur-Marne',
                    'ivry': 'Ivry-sur-Seine',
                    'bonneuil': 'Bonneuil-sur-Marne'
                };
                return labels[location] || location;
            }

            // Format metier label for display
            function formatMetierLabel(metier) {
                const labels = {
                    'educateur': 'Éducateur',
                    'aide-soignant': 'Aide-soignant',
                    'infirmier': 'Infirmier',
                    'moniteur': 'Moniteur',
                    'psychologue': 'Psychologue',
                    'directeur': 'Directeur'
                };
                return labels[metier] || metier;
            }

            // ----------------------------------------------------------------------
            // FILTERING LOGIC
            // ----------------------------------------------------------------------

            function applyFilters() {
                // Show loading state
                elements.jobsGrid.innerHTML = '';
                elements.loading.classList.remove('hidden');
                elements.jobsGrid.classList.add('hidden');
                elements.emptyState.classList.add('hidden');

                // Simulate API delay for realistic UX
                setTimeout(() => {
                    let filtered = [...jobsData];

                    // Apply search filter
                    if (state.filters.search) {
                        const searchLower = state.filters.search.toLowerCase();
                        filtered = filtered.filter(job => 
                            job.title.toLowerCase().includes(searchLower) ||
                            job.description.toLowerCase().includes(searchLower) ||
                            job.tags.some(tag => tag.toLowerCase().includes(searchLower))
                        );
                    }

                    // Apply type filter
                    if (state.filters.type) {
                        filtered = filtered.filter(job => job.type === state.filters.type);
                    }

                    // Apply location filter
                    if (state.filters.location) {
                        filtered = filtered.filter(job => job.locationSlug === state.filters.location);
                    }

                    // Apply metier filter
                    if (state.filters.metier) {
                        filtered = filtered.filter(job => job.metier === state.filters.metier);
                    }

                    // Update UI
                    renderJobs(filtered);
                    updateJobsCount(filtered.length);
                    updateActiveChips();
                    toggleClearFiltersButton();

                    // Hide loading
                    elements.loading.classList.add('hidden');
                }, 300);
            }

            function renderJobs(jobs) {
                if (jobs.length === 0) {
                    elements.jobsGrid.classList.add('hidden');
                    elements.emptyState.classList.remove('hidden');
                    return;
                }

                elements.emptyState.classList.add('hidden');
                elements.jobsGrid.classList.remove('hidden');

                // Render job cards
                elements.jobsGrid.innerHTML = jobs.map(job => createJobCard(job)).join('');
            }

            function createJobCard(job) {
                return `
                    <article class="job-card" role="listitem" tabindex="0" aria-labelledby="job-title-${job.id}">
                        <div class="job-header">
                            <div>
                                <h3 class="job-title">
                                    <a href="offre-detail.html?id=${job.id}" id="job-title-${job.id}">
                                        ${escapeHtml(job.title)}
                                    </a>
                                </h3>
                                <div class="job-meta">
                                    <span class="meta-item">
                                        <svg width="16" height="16" viewBox="0 0 16 16" fill="none" xmlns="http://www.w3.org/2000/svg" aria-hidden="true">
                                            <path d="M8 1C5.5 1 3.5 3 3.5 5.5C3.5 8.5 8 14 8 14C8 14 12.5 8.5 12.5 5.5C12.5 3 10.5 1 8 1Z" stroke="#636363" stroke-width="1.5"/>
                                            <circle cx="8" cy="5.5" r="2" stroke="#636363" stroke-width="1.5"/>
                                        </svg>
                                        ${escapeHtml(job.location)}
                                    </span>
                                    <span class="meta-item">
                                        <svg width="16" height="16" viewBox="0 0 16 16" fill="none" xmlns="http://www.w3.org/2000/svg" aria-hidden="true">
                                            <circle cx="8" cy="4" r="2" stroke="#636363" stroke-width="1.5"/>
                                            <path d="M2 12C2 9.23858 4.23858 7 8 7C11.7614 7 14 9.23858 14 12" stroke="#636363" stroke-width="1.5" stroke-linecap="round"/>
                                        </svg>
                                        ${escapeHtml(job.experience)}
                                    </span>
                                    <span class="meta-item">
                                        <svg width="16" height="16" viewBox="0 0 16 16" fill="none" xmlns="http://www.w3.org/2000/svg" aria-hidden="true">
                                            <rect x="2" y="2" width="12" height="12" rx="2" stroke="#636363" stroke-width="1.5"/>
                                            <path d="M6 6H10" stroke="#636363" stroke-width="1.5" stroke-linecap="round"/>
                                            <path d="M6 10H8" stroke="#636363" stroke-width="1.5" stroke-linecap="round"/>
                                        </svg>
                                        ${escapeHtml(job.posted)}
                                    </span>
                                </div>
                            </div>
                            <div class="job-tags">
                                <span class="job-tag contract-type">${escapeHtml(job.typeLabel)}</span>
                                <span class="job-tag location">${escapeHtml(job.location)}</span>
                            </div>
                        </div>
                        <p class="job-description">${escapeHtml(job.description)}</p>
                        <div class="job-tags">
                            ${job.tags.map(tag => `<span class="job-tag">${escapeHtml(tag)}</span>`).join('')}
                        </div>
                        <div class="job-actions">
                            <a href="offre-detail.html?id=${job.id}" class="btn btn-outline">Voir l'offre</a>
                            <a href="postuler.html?id=${job.id}" class="btn btn-primary">Postuler</a>
                        </div>
                    </article>
                `;
            }

            function updateJobsCount(count) {
                const label = count > 1 ? 'postes' : 'poste';
                elements.jobsCount.textContent = `${count} ${label} disponible${count > 1 ? 's' : ''}`;
            }

            function updateActiveChips() {
                const chips = [];

                if (state.filters.type) {
                    chips.push({
                        key: 'type',
                        label: formatTypeLabel(state.filters.type),
                        value: state.filters.type
                    });
                }

                if (state.filters.location) {
                    chips.push({
                        key: 'location',
                        label: formatLocationLabel(state.filters.location),
                        value: state.filters.location
                    });
                }

                if (state.filters.metier) {
                    chips.push({
                        key: 'metier',
                        label: formatMetierLabel(state.filters.metier),
                        value: state.filters.metier
                    });
                }

                if (state.filters.search) {
                    chips.push({
                        key: 'search',
                        label: `"${state.filters.search}"`,
                        value: state.filters.search
                    });
                }

                state.activeChips = chips;

                if (chips.length > 0) {
                    elements.activeFiltersContainer.innerHTML = chips.map(chip => `
                        <button class="filter-chip active" data-key="${chip.key}" data-value="${chip.value}" aria-label="Supprimer le filtre ${chip.label}">
                            ${chip.label}
                            <span class="remove" aria-hidden="true">×</span>
                        </button>
                    `).join('');
                } else {
                    elements.activeFiltersContainer.innerHTML = '';
                }
            }

            function toggleClearFiltersButton() {
                const hasFilters = state.filters.type || 
                                  state.filters.location || 
                                  state.filters.metier || 
                                  state.filters.search;
                elements.clearFiltersBtn.hidden = !hasFilters;
            }

            function clearAllFilters() {
                state.filters = {
                    search: '',
                    type: '',
                    location: '',
                    metier: ''
                };

                // Reset UI controls
                elements.searchInput.value = '';
                elements.typeSelect.value = '';
                elements.locationSelect.value = '';
                elements.metierSelect.value = '';

                applyFilters();
            }

            // ----------------------------------------------------------------------
            // EVENT LISTENERS
            // ----------------------------------------------------------------------

            // Search input with debounce
            const debouncedSearch = debounce((value) => {
                state.filters.search = value.trim();
                applyFilters();
            }, 300);

            elements.searchInput.addEventListener('input', (e) => {
                debouncedSearch(e.target.value);
            });

            // Filter selects
            elements.typeSelect.addEventListener('change', (e) => {
                state.filters.type = e.target.value;
                applyFilters();
            });

            elements.locationSelect.addEventListener('change', (e) => {
                state.filters.location = e.target.value;
                applyFilters();
            });

            elements.metierSelect.addEventListener('change', (e) => {
                state.filters.metier = e.target.value;
                applyFilters();
            });

            // Clear filters button
            elements.clearFiltersBtn.addEventListener('click', clearAllFilters);
            elements.resetFromEmpty.addEventListener('click', clearAllFilters);

            // Active filter chip removal
            elements.activeFiltersContainer.addEventListener('click', (e) => {
                if (e.target.classList.contains('remove') || e.target.closest('.remove')) {
                    const chip = e.target.closest('.filter-chip');
                    const key = chip.dataset.key;

                    // Remove from state
                    if (key === 'search') {
                        state.filters.search = '';
                        elements.searchInput.value = '';
                    } else {
                        state.filters[key] = '';
                        // Reset corresponding select
                        if (elements[`${key}Select`]) {
                            elements[`${key}Select`].value = '';
                        }
                    }

                    applyFilters();
                }
            });

            // Keyboard navigation for job cards
            elements.jobsGrid.addEventListener('keydown', (e) => {
                if (e.key === 'Enter' || e.key === ' ') {
                    const card = e.target.closest('.job-card');
                    if (card) {
                        const link = card.querySelector('a[href]');
                        if (link) {
                            e.preventDefault();
                            window.location.href = link.href;
                        }
                    }
                }
            });

            // Story card interactions
            document.querySelectorAll('.story-card').forEach(card => {
                card.addEventListener('click', () => {
                    alert('🎬 Vidéo témoignage - Fonctionnalité à implémenter avec modal/lightbox');
                });

                card.addEventListener('keydown', (e) => {
                    if (e.key === 'Enter' || e.key === ' ') {
                        e.preventDefault();
                        alert('🎬 Vidéo témoignage - Fonctionnalité à implémenter avec modal/lightbox');
                    }
                });
            });

            // ----------------------------------------------------------------------
            // INITIALIZE
            // ----------------------------------------------------------------------

            // Initial render
            applyFilters();

            console.log('✅ Apogei 94 Careers Page initialized');
            console.log('📊 Design tokens loaded from tokens.json & theme.json');
            console.log('♿ Accessibility features enabled');
        });
    </script>
