        .section-padding { padding: var(--space-3xl) 0; }
        .text-brand { color: var(--color-text-brand); }
        .bg-surface { background-color: var(--color-bg-surface); }
        .bg-brand { background-color: var(--color-bg-brand); }
        .text-inverse { color: var(--color-text-inverse); }

        /* Buttons */
        .btn {
            display: inline-flex; align-items: center; justify-content: center; gap: var(--space-xs);
            padding: var(--space-xs) var(--space-sm); font-family: var(--font-family);
            font-size: var(--font-size-body-sm); font-weight: var(--font-weight-semibold);
            border-radius: var(--radius-md); border: 1px solid transparent; cursor: pointer;
            transition: all var(--duration-normal) var(--easing-default);
            min-height: 44px; min-width: 44px; text-decoration: none;
        }

        .btn-primary {
            background-color: var(--color-bg-brand); color: var(--color-text-inverse);
            border-color: var(--color-bg-brand);
        }

        .btn-primary:hover {
            background-color: var(--color-interactive-hover); border-color: var(--color-interactive-hover);
            transform: translateY(-2px); box-shadow: var(--shadow-md);
        }

        .btn-outline {
            background-color: transparent; color: var(--color-text-brand);
            border-color: var(--color-border-brand);
        }

        .btn-outline:hover {
            background-color: var(--color-bg-brand-subtle); transform: translateY(-2px);
