        }

        .btn-outline-white:hover { background-color: rgba(255, 255, 255, 0.1); color: var(--color-text-inverse); }

        /* Job Content */
        .job-content { padding: var(--space-3xl) 0; }

        .job-grid {
            display: grid; grid-template-columns: 1fr 380px; gap: var(--space-3xl);
        }

        .job-main { min-width: 0; }

        .content-section {
            background-color: var(--color-bg-surface); border: 1px solid var(--color-border-default);
