        .content-section-text:last-child { margin-bottom: 0; }

        .job-details-list {
            list-style: none; display: grid; grid-template-columns: 1fr 1fr; gap: var(--space-md);
        }

        .job-details-list li {
            display: flex; align-items: flex-start; gap: var(--space-sm);
            padding: var(--space-sm) 0; font-size: var(--font-size-body-sm);
        }

