        /* Sidebar */
        .job-sidebar { position: sticky; top: 100px; height: fit-content; }

        .sidebar-card {
            background-color: var(--color-bg-surface); border: 1px solid var(--color-border-default);
            border-radius: var(--radius-xl); padding: var(--space-lg); margin-bottom: var(--space-lg);
