        }

        .related-job-title {
            font-size: var(--font-size-h4); margin-bottom: var(--space-xs);
        }

        .related-job-title a { color: var(--color-text-default); }
        .related-job-title a:hover { color: var(--color-text-brand); }

        .related-job-meta {
            font-size: var(--font-size-body-sm); color: var(--color-text-secondary);
            margin-bottom: var(--space-sm);
        }

        .related-job-tags {
            display: flex; gap: var(--space-xs); flex-wrap: wrap;
        }

        .job-tag {
            padding: var(--space-2xs) var(--space-xs); background-color: var(--color-bg-subtle);
            color: var(--color-text-secondary); font-size: var(--font-size-caption);
