            border-radius: var(--radius-md); border: 1px solid transparent; cursor: pointer;
            transition: all var(--duration-normal) var(--easing-default);
            min-height: 44px; min-width: 44px; text-decoration: none;
        }

        .btn-primary {
            background-color: var(--color-bg-brand); color: var(--color-text-inverse);
            border-color: var(--color-bg-brand);
        }

        .btn-primary:hover {
            background-color: var(--color-interactive-hover); border-color: var(--color-interactive-hover);
            transform: translateY(-2px); box-shadow: var(--shadow-md);
        }

        .btn-primary:disabled {
