        .btn-outline {
            background-color: transparent; color: var(--color-text-brand);
            border-color: var(--color-border-brand);
        }

        .btn-outline:hover {
            background-color: var(--color-bg-brand-subtle); transform: translateY(-2px);
        }

        .btn-lg { padding: var(--space-sm) var(--space-md); font-size: var(--font-size-body-lg); }

        /* Header */
        .header {
            background-color: var(--color-bg-surface); border-bottom: 1px solid var(--color-border-default);
            position: sticky; top: 0; z-index: 1100; height: 80px; display: flex; align-items: center;
        }

        .header-content {
            display: flex; justify-content: space-between; align-items: center; width: 100%;
        }

        .logo {
            font-size: 1.5rem; font-weight: var(--font-weight-bold); color: var(--color-text-brand);
            display: flex; align-items: center; gap: var(--space-xs); text-decoration: none;
        }

        .nav-links { display: flex; gap: var(--space-xl); list-style: none; }
        .nav-links a { font-weight: var(--font-weight-semibold); color: var(--color-text-default); }
        .nav-links a:hover, .nav-links a.active { color: var(--color-text-brand); }
        .nav-links { display: none; }

        /* Breadcrumb */
        .breadcrumb {
            padding: var(--space-lg) 0; background-color: var(--color-bg-surface);
            border-bottom: 1px solid var(--color-border-subtle);
        }

        .breadcrumb-list {
            display: flex; align-items: center; gap: var(--space-sm); list-style: none;
            font-size: var(--font-size-body-sm); color: var(--color-text-secondary); flex-wrap: wrap;
        }

        .breadcrumb-list a { color: var(--color-text-brand); }
        .breadcrumb-list a:hover { text-decoration: underline; }

        /* Form Page */
        .form-page { padding: var(--space-3xl) 0; }

        .form-container {
            max-width: var(--container-content); margin: 0 auto;
        }

        .form-header {
            text-align: center; margin-bottom: var(--space-3xl);
        }

        .form-header h1 {
            font-size: var(--font-size-h1); margin-bottom: var(--space-md);
        }

        .form-header p {
            font-size: var(--font-size-body-lg); color: var(--color-text-secondary);
        }

        .job-summary {
            background-color: var(--color-bg-brand-subtle); border-radius: var(--radius-xl);
            padding: var(--space-lg); margin-bottom: var(--space-2xl);
            display: flex; align-items: center; gap: var(--space-md);
        }

        .job-summary-icon {
            width: 48px; height: 48px; background-color: var(--color-bg-brand);
            border-radius: var(--radius-md); display: flex; align-items: center;
            justify-content: center; color: var(--color-text-inverse); flex-shrink: 0;
        }

        .job-summary-content h3 {
            font-size: var(--font-size-h4); margin-bottom: var(--space-2xs);
        }

        .job-summary-content p {
            font-size: var(--font-size-body-sm); color: var(--color-text-secondary);
        }

        /* Form Sections */
        .form-section {
            background-color: var(--color-bg-surface); border: 1px solid var(--color-border-default);
            border-radius: var(--radius-xl); padding: var(--space-xl); margin-bottom: var(--space-lg);
        }

        .form-section-title {
            font-size: var(--font-size-h3); margin-bottom: var(--space-lg);
            padding-bottom: var(--space-md); border-bottom: 1px solid var(--color-border-subtle);
            display: flex; align-items: center; gap: var(--space-sm);
        }

        .form-section-title svg {
            color: var(--color-text-brand); flex-shrink: 0;
        }

        /* Form Groups */
        .form-grid {
            display: grid; grid-template-columns: 1fr 1fr; gap: var(--space-md);
        }

        .form-group {
            display: flex; flex-direction: column; gap: var(--space-2xs);
        }

        .form-group.full-width { grid-column: 1 / -1; }

        .form-label {
            font-size: var(--font-size-body-sm); font-weight: var(--font-weight-semibold);
            color: var(--color-text-default); display: flex; align-items: center; gap: var(--space-2xs);
        }

        .form-label .required { color: var(--color-text-error); }

        .form-input, .form-textarea, .form-select, .form-file {
            padding: var(--space-xs) var(--space-sm); border: 1px solid var(--color-border-strong);
            border-radius: var(--radius-md); font-size: var(--font-size-body-sm);
            font-family: var(--font-family); min-height: 44px;
            background-color: var(--color-bg-surface);
            transition: border-color var(--duration-normal) var(--easing-default), box-shadow var(--duration-normal) var(--easing-default);
        }

        .form-input:focus, .form-textarea:focus, .form-select:focus, .form-file:focus {
            border-color: var(--color-border-focus); outline: none;
            box-shadow: var(--shadow-focus);
        }

        .form-input.error, .form-textarea.error, .form-select.error {
            border-color: var(--color-border-error); box-shadow: var(--shadow-focus-error);
        }

        .form-input.success, .form-textarea.success, .form-select.success {
            border-color: var(--color-border-success); box-shadow: var(--shadow-focus-success);
        }

        .form-textarea { min-height: 120px; resize: vertical; font-family: var(--font-family); }

        .form-help {
            font-size: var(--font-size-caption); color: var(--color-text-muted);
        }

        .form-error {
            font-size: var(--font-size-caption); color: var(--color-text-error);
            display: none;
        }

        .form-error.visible { display: block; }

        .form-file {
            padding: var(--space-sm); cursor: pointer;
        }

        .form-file-wrapper {
            border: 2px dashed var(--color-border-strong); border-radius: var(--radius-md);
            padding: var(--space-lg); text-align: center;
            transition: all var(--duration-normal) var(--easing-default);
        }

        .form-file-wrapper:hover {
            border-color: var(--color-border-brand); background-color: var(--color-bg-brand-subtle);
        }

        .form-file-wrapper svg {
            width: 48px; height: 48px; color: var(--color-text-muted); margin-bottom: var(--space-sm);
        }

        .form-file-wrapper p {
            font-size: var(--font-size-body-sm); color: var(--color-text-secondary);
            margin-bottom: var(--space-xs);
        }

        .form-file-wrapper .file-types {
            font-size: var(--font-size-caption); color: var(--color-text-muted);
        }

        /* Progress Steps */
        .progress-steps {
            display: flex; justify-content: space-between; margin-bottom: var(--space-2xl);
            position: relative;
        }

        .progress-steps::before {
            content: ''; position: absolute; top: 24px; left: 0; right: 0; height: 2px;
            background-color: var(--color-border-default); z-index: 0;
        }

        .progress-step {
            display: flex; flex-direction: column; align-items: center; gap: var(--space-xs);
            position: relative; z-index: 1; flex: 1;
        }

        .progress-step-number {
            width: 48px; height: 48px; border-radius: 50%;
            background-color: var(--color-bg-surface); border: 2px solid var(--color-border-default);
            display: flex; align-items: center; justify-content: center;
            font-size: var(--font-size-body); font-weight: var(--font-weight-semibold);
            color: var(--color-text-muted); transition: all var(--duration-normal) var(--easing-default);
        }

        .progress-step.active .progress-step-number {
            border-color: var(--color-border-brand); background-color: var(--color-bg-brand);
            color: var(--color-text-inverse);
        }

        .progress-step.completed .progress-step-number {
            border-color: var(--color-border-success); background-color: var(--color-bg-success);
            color: var(--color-text-inverse);
        }

        .progress-step-label {
            font-size: var(--font-size-body-sm); color: var(--color-text-secondary);
            font-weight: var(--font-weight-medium);
        }

        .progress-step.active .progress-step-label {
            color: var(--color-text-brand); font-weight: var(--font-weight-semibold);
        }

        /* Form Actions */
        .form-actions {
            display: flex; justify-content: space-between; gap: var(--space-md);
            margin-top: var(--space-xl); padding-top: var(--space-xl);
            border-top: 1px solid var(--color-border-subtle);
        }

        .form-actions .btn { min-width: 160px; }

        /* Success Message */
        .success-message {
            background-color: var(--color-bg-success-subtle); border: 1px solid var(--color-border-success);
            border-radius: var(--radius-xl); padding: var(--space-2xl); text-align: center;
            display: none;
        }

        .success-message.visible { display: block; }

        .success-icon {
            width: 80px; height: 80px; margin: 0 auto var(--space-lg);
            background-color: var(--color-bg-success); border-radius: 50%;
            display: flex; align-items: center; justify-content: center;
            color: var(--color-text-inverse);
        }

        .success-message h2 {
            font-size: var(--font-size-h2); margin-bottom: var(--space-md);
            color: var(--color-text-success);
        }

        .success-message p {
            font-size: var(--font-size-body-lg); color: var(--color-text-secondary);
            margin-bottom: var(--space-xl);
        }

        /* Footer */
        .footer {
            background-color: var(--color-bg-surface); border-top: 1px solid var(--color-border-default);
            padding: var(--space-3xl) 0; margin-top: var(--space-3xl);
        }

        .footer-grid {
            display: grid; grid-template-columns: repeat(4, 1fr); gap: var(--space-xl);
        }

        .footer-col h4 {
            font-size: var(--font-size-body); margin-bottom: var(--space-lg);
            color: var(--color-text-brand);
        }

        .footer-col ul { list-style: none; }
        .footer-col li { margin-bottom: var(--space-sm); }
        .footer-col a {
            color: var(--color-text-secondary); font-size: var(--font-size-body-sm);
        }
        .footer-col a:hover { color: var(--color-text-brand); text-decoration: underline; }

        /* Responsive */
        @media (max-width: 768px) {
            :root { --font-size-h1: 2rem; --space-3xl: 2rem; }
            .form-grid { grid-template-columns: 1fr; }
            .progress-steps { flex-direction: column; gap: var(--space-md); }
            .progress-steps::before { display: none; }
            .progress-step { flex-direction: row; }
            .footer-grid { grid-template-columns: 1fr; }
            .form-actions { flex-direction: column; }
            .form-actions .btn { width: 100%; }
            .job-summary { flex-direction: column; text-align: center; }
        }
    </style>
</head>
