    <script>
        let currentStep = 1;
        const totalSteps = 3;

        function updateProgress(step) {
            document.querySelectorAll('.progress-step').forEach((stepEl, index) => {
                const stepNum = index + 1;
                stepEl.classList.remove('active', 'completed');
                if (stepNum < step) {
                    stepEl.classList.add('completed');
                } else if (stepNum === step) {
                    stepEl.classList.add('active');
                }
            });
            document.querySelector('.progress-steps').setAttribute('aria-valuenow', step);
        }

        function showStep(step) {
            document.querySelectorAll('.form-section').forEach(section => {
                section.style.display = 'none';
            });
            document.querySelector(`.form-section[data-step="${step}"]`).style.display = 'block';
            updateProgress(step);
            currentStep = step;
        }

        function nextStep(nextStepNum) {
            if (validateStep(currentStep)) {
                showStep(nextStepNum);
                window.scrollTo({ top: 0, behavior: 'smooth' });
            }
        }

        function previousStep(prevStepNum) {
            showStep(prevStepNum);
            window.scrollTo({ top: 0, behavior: 'smooth' });
        }

        function validateStep(step) {
            let isValid = true;

            if (step === 1) {
                const requiredFields = ['prenom', 'nom', 'email', 'telephone'];
                requiredFields.forEach(fieldId => {
                    const field = document.getElementById(fieldId);
                    const errorEl = document.getElementById(`${fieldId}-error`);
                    if (!field.value.trim()) {
                        field.classList.add('error');
                        if (errorEl) errorEl.classList.add('visible');
                        isValid = false;
                    } else {
                        field.classList.remove('error');
                        if (errorEl) errorEl.classList.remove('visible');
                    }
                });

                // Email validation
                const emailField = document.getElementById('email');
                const emailError = document.getElementById('email-error');
                const emailPattern = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
                if (emailField.value && !emailPattern.test(emailField.value)) {
                    emailField.classList.add('error');
                    emailError.classList.add('visible');
                    isValid = false;
                }
            }

            if (step === 2) {
                const messageField = document.getElementById('message');
                const messageError = document.getElementById('message-error');
                if (messageField.value.trim().length < 100) {
                    messageField.classList.add('error');
                    messageError.classList.add('visible');
                    isValid = false;
                } else {
                    messageField.classList.remove('error');
                    messageError.classList.remove('visible');
                }

                const experienceField = document.getElementById('experience');
                const experienceError = document.getElementById('experience-error');
                if (experienceField.value.trim().length < 50) {
                    experienceField.classList.add('error');
                    experienceError.classList.add('visible');
                    isValid = false;
                } else {
                    experienceField.classList.remove('error');
                    experienceError.classList.remove('visible');
                }

                const disponibiliteField = document.getElementById('disponibilite');
                const disponibiliteError = document.getElementById('disponibilite-error');
                if (!disponibiliteField.value) {
                    disponibiliteField.classList.add('error');
                    disponibiliteError.classList.add('visible');
                    isValid = false;
                } else {
                    disponibiliteField.classList.remove('error');
                    disponibiliteError.classList.remove('visible');
                }
            }

            if (step === 3) {
                const cvField = document.getElementById('cv');
                const cvError = document.getElementById('cv-error');
                if (!cvField.files.length) {
                    cvError.classList.add('visible');
                    isValid = false;
                } else {
                    cvError.classList.remove('visible');
                }

                const lettreField = document.getElementById('lettre');
                const lettreError = document.getElementById('lettre-error');
                if (!lettreField.files.length) {
                    lettreError.classList.add('visible');
                    isValid = false;
                } else {
                    lettreError.classList.remove('visible');
                }

                const rgpdField = document.getElementById('rgpd');
                const rgpdError = document.getElementById('rgpd-error');
                if (!rgpdField.checked) {
                    rgpdError.classList.add('visible');
                    isValid = false;
                } else {
                    rgpdError.classList.remove('visible');
                }
            }

            return isValid;
        }

        // Form submission
        document.getElementById('application-form').addEventListener('submit', function(e) {
            e.preventDefault();

            if (validateStep(3)) {
                // Show success message
                document.getElementById('application-form').style.display = 'none';
                document.querySelector('.progress-steps').style.display = 'none';
                document.querySelector('.job-summary').style.display = 'none';
                document.querySelector('.form-header').style.display = 'none';
                document.getElementById('success-message').classList.add('visible');

                // In production, submit to server here
                console.log('✅ Form submitted successfully');
            }
        });

        // Real-time validation
        document.querySelectorAll('.form-input, .form-textarea, .form-select').forEach(field => {
            field.addEventListener('blur', function() {
                if (this.value.trim()) {
                    this.classList.remove('error');
                    this.classList.add('success');
                }
            });
        });

        // Initialize
        showStep(1);
        console.log('✅ Application Form initialized');
    </script>
</body>
</html>
