            display: inline-block; padding: var(--space-xs) var(--space-md);
            background-color: rgba(255, 255, 255, 0.2); border-radius: var(--radius-full);
            font-size: var(--font-size-body-sm); font-weight: var(--font-weight-semibold);
            margin-bottom: var(--space-lg); backdrop-filter: blur(4px);
        }

        .donate-hero-title {
            font-size: var(--font-size-display-sm); margin-bottom: var(--space-lg);
            line-height: var(--line-height-heading);
        }

        .donate-hero-desc {
            font-size: var(--font-size-body-lg); opacity: 0.95; margin-bottom: var(--space-2xl);
            max-width: 540px; margin-left: auto; margin-right: auto;
        }

        .tax-info {
            display: inline-flex; align-items: center; gap: var(--space-xs);
            background-color: rgba(255, 255, 255, 0.15); padding: var(--space-sm) var(--space-md);
            border-radius: var(--radius-lg); font-size: var(--font-size-body-sm);
        }

        /* Impact Calculator */
        .impact-calculator {
            background-color: var(--color-bg-surface); border-radius: var(--radius-xl);
            padding: var(--space-2xl); margin: calc(-1 * var(--space-3xl)) auto var(--space-3xl);
            max-width: 900px; box-shadow: var(--shadow-lg); position: relative; z-index: 10;
        }

        .calculator-title {
            text-align: center; margin-bottom: var(--space-xl);
        }

        .calculator-title h2 {
            font-size: var(--font-size-h2); margin-bottom: var(--space-sm);
        }

        .calculator-title p {
            color: var(--color-text-secondary); font-size: var(--font-size-body-lg);
        }

        .donation-amounts {
            display: grid; grid-template-columns: repeat(auto-fit, minmax(140px, 1fr));
            gap: var(--space-md); margin-bottom: var(--space-xl);
        }

        .amount-btn {
            padding: var(--space-md) var(--space-lg); border: 2px solid var(--color-border-default);
            border-radius: var(--radius-lg); background-color: var(--color-bg-surface);
            font-size: var(--font-size-body-lg); font-weight: var(--font-weight-semibold);
            color: var(--color-text-default); cursor: pointer;
            transition: all var(--duration-normal) var(--easing-default);
            min-height: 64px;
        }

        .amount-btn:hover {
            border-color: var(--color-border-brand); background-color: var(--color-bg-brand-subtle);
        }

        .amount-btn.active {
            border-color: var(--color-border-brand); background-color: var(--color-bg-brand);
            color: var(--color-text-inverse);
        }

        .custom-amount {
            margin-bottom: var(--space-xl);
        }

        .custom-amount label {
            display: block; font-size: var(--font-size-body-sm); font-weight: var(--font-weight-semibold);
            margin-bottom: var(--space-xs); color: var(--color-text-default);
        }

        .custom-amount-input {
            width: 100%; padding: var(--space-sm) var(--space-md);
            border: 1px solid var(--color-border-strong); border-radius: var(--radius-md);
            font-size: var(--font-size-body-lg); font-family: var(--font-family);
            min-height: 52px;
        }

        .custom-amount-input:focus {
            border-color: var(--color-border-focus); outline: none;
            box-shadow: var(--shadow-focus);
        }

        .frequency-toggle {
            display: flex; gap: var(--space-sm); margin-bottom: var(--space-xl);
            background-color: var(--color-bg-subtle); padding: var(--space-2xs);
            border-radius: var(--radius-md);
        }

        .frequency-btn {
            flex: 1; padding: var(--space-sm) var(--space-md); border: none;
            background-color: transparent; font-size: var(--font-size-body);
            font-weight: var(--font-weight-semibold); color: var(--color-text-secondary);
            cursor: pointer; border-radius: var(--radius-sm);
            transition: all var(--duration-normal) var(--easing-default);
            min-height: 44px;
        }

        .frequency-btn:hover { color: var(--color-text-default); }

        .frequency-btn.active {
            background-color: var(--color-bg-surface); color: var(--color-text-brand);
            box-shadow: var(--shadow-sm);
        }

        .impact-display {
            background-color: var(--color-bg-brand-subtle); border-radius: var(--radius-xl);
            padding: var(--space-xl); text-align: center;
        }

        .impact-display h3 {
            font-size: var(--font-size-h3); margin-bottom: var(--space-md);
            color: var(--color-text-brand);
        }

        .impact-items {
            display: grid; grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
            gap: var(--space-lg); margin-top: var(--space-lg);
        }

        .impact-item {
            display: flex; flex-direction: column; align-items: center; gap: var(--space-sm);
        }

        .impact-icon {
            width: 64px; height: 64px; background-color: var(--color-bg-brand);
            border-radius: var(--radius-lg); display: flex; align-items: center;
            justify-content: center; color: var(--color-text-inverse);
        }

        .impact-label {
            font-size: var(--font-size-body-sm); color: var(--color-text-secondary);
            text-align: center;
        }

        .impact-value {
            font-size: var(--font-size-body-lg); font-weight: var(--font-weight-semibold);
            color: var(--color-text-brand);
        }

        .donate-form {
            margin-top: var(--space-xl); padding-top: var(--space-xl);
            border-top: 1px solid var(--color-border-subtle);
        }

        .donate-form .btn { width: 100%; justify-content: center; }

        /* Impact Stories */
        .impact-stories { padding: var(--space-4xl) 0; }

        .stories-header {
            text-align: center; max-width: var(--container-content); margin: 0 auto var(--space-3xl);
        }

        .stories-header h2 {
            font-size: var(--font-size-h2); margin-bottom: var(--space-md);
        }

        .stories-header p {
            color: var(--color-text-secondary); font-size: var(--font-size-body-lg);
        }

        .stories-grid {
            display: grid; grid-template-columns: repeat(auto-fit, minmax(320px, 1fr));
            gap: var(--space-xl);
        }

        .story-card {
            background-color: var(--color-bg-surface); border: 1px solid var(--color-border-default);
            border-radius: var(--radius-xl); overflow: hidden;
            transition: all var(--duration-slow) var(--easing-default);
        }

        .story-card:hover {
            transform: translateY(-4px); box-shadow: var(--shadow-lg);
            border-color: var(--color-border-brand);
        }

        .story-media {
            height: 200px; background-color: var(--color-bg-muted); overflow: hidden;
        }

        .story-media img {
            width: 100%; height: 100%; object-fit: cover; border-radius: 0;
        }

        .story-content { padding: var(--space-lg); }

        .story-donation {
            display: inline-block; padding: var(--space-2xs) var(--space-sm);
            background-color: var(--color-bg-accent-green-subtle);
            color: var(--color-text-success); font-size: var(--font-size-caption);
            font-weight: var(--font-weight-semibold); border-radius: var(--radius-full);
            margin-bottom: var(--space-sm);
        }

        .story-title {
            font-size: var(--font-size-h3); margin-bottom: var(--space-sm);
        }

        .story-excerpt {
            font-size: var(--font-size-body-sm); color: var(--color-text-secondary);
            line-height: var(--line-height-body); margin-bottom: var(--space-md);
        }

        /* Transparency Section */
        .transparency-section {
            background-color: var(--color-bg-surface); padding: var(--space-4xl) 0;
        }

        .transparency-grid {
            display: grid; grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
            gap: var(--space-xl);
        }

        .transparency-card {
            text-align: center; padding: var(--space-xl);
        }

        .transparency-icon {
            width: 80px; height: 80px; margin: 0 auto var(--space-lg);
            background-color: var(--color-bg-brand-subtle); border-radius: var(--radius-xl);
            display: flex; align-items: center; justify-content: center;
            color: var(--color-text-brand);
        }

        .transparency-title {
            font-size: var(--font-size-h4); margin-bottom: var(--space-sm);
        }

        .transparency-value {
            font-size: var(--font-size-display-sm); font-weight: var(--font-weight-bold);
            color: var(--color-text-brand); margin-bottom: var(--space-xs);
        }

        .transparency-desc {
            font-size: var(--font-size-body-sm); color: var(--color-text-secondary);
        }

        /* FAQ Section */
        .faq-section { padding: var(--space-4xl) 0; }

        .faq-header {
            text-align: center; max-width: var(--container-content); margin: 0 auto var(--space-3xl);
        }

        .faq-grid {
            display: grid; grid-template-columns: repeat(auto-fit, minmax(320px, 1fr));
            gap: var(--space-lg); max-width: 900px; margin: 0 auto;
        }

        .faq-item {
            background-color: var(--color-bg-surface); border: 1px solid var(--color-border-default);
            border-radius: var(--radius-xl); padding: var(--space-lg);
        }

        .faq-question {
            font-size: var(--font-size-h4); margin-bottom: var(--space-sm);
            display: flex; justify-content: space-between; align-items: flex-start; gap: var(--space-md);
        }

        .faq-answer {
            font-size: var(--font-size-body-sm); color: var(--color-text-secondary);
            line-height: var(--line-height-body);
        }

        /* CTA Section */
        .cta-section {
            background-color: var(--color-bg-brand); color: var(--color-text-inverse);
            border-radius: var(--radius-xl); padding: var(--space-3xl); text-align: center;
            margin: var(--space-3xl) auto; max-width: 1000px;
        }

        .cta-section h2 {
            color: var(--color-text-inverse); margin-bottom: var(--space-sm);
        }

        .cta-section p {
            opacity: 0.9; margin-bottom: var(--space-xl); max-width: 600px;
            margin-left: auto; margin-right: auto;
        }

        .cta-buttons {
            display: flex; gap: var(--space-md); justify-content: center; flex-wrap: wrap;
        }

        .btn-white {
            background-color: var(--color-text-inverse); color: var(--color-bg-brand);
        }

        .btn-white:hover { background-color: #f0f0f0; color: var(--color-bg-brand); }

        .btn-outline-white {
            background-color: transparent; color: var(--color-text-inverse);
            border-color: var(--color-text-inverse);
        }

        .btn-outline-white:hover {
            background-color: rgba(255, 255, 255, 0.1); color: var(--color-text-inverse);
        }

        /* Footer */
        .footer {
            background-color: var(--color-bg-surface); border-top: 1px solid var(--color-border-default);
            padding: var(--space-3xl) 0; margin-top: var(--space-3xl);
        }

        .footer-grid {
            display: grid; grid-template-columns: repeat(4, 1fr); gap: var(--space-xl);
        }

        .footer-col h4 {
            font-size: var(--font-size-body); margin-bottom: var(--space-lg);
            color: var(--color-text-brand);
        }

        .footer-col ul { list-style: none; }
        .footer-col li { margin-bottom: var(--space-sm); }
        .footer-col a {
            color: var(--color-text-secondary); font-size: var(--font-size-body-sm);
        }
        .footer-col a:hover { color: var(--color-text-brand); text-decoration: underline; }

        /* Responsive */
        @media (max-width: 1024px) {
            .impact-calculator { margin: var(--space-2xl) auto; }
            .footer-grid { grid-template-columns: 1fr 1fr; }
            .nav-links { display: none; }
        }

        @media (max-width: 768px) {
            :root { --font-size-h1: 2rem; --space-3xl: 2rem; --space-4xl: 3rem; }
            .donate-hero-title { font-size: var(--font-size-display-sm); }
            .donation-amounts { grid-template-columns: repeat(2, 1fr); }
            .impact-items { grid-template-columns: 1fr; }
            .cta-buttons { flex-direction: column; }
            .cta-buttons .btn { width: 100%; }
            .footer-grid { grid-template-columns: 1fr; }
        }

        @media (max-width: 480px) {
            .donation-amounts { grid-template-columns: 1fr; }
            .frequency-toggle { flex-direction: column; }
        }
    </style>
</head>
