            <div class="footer-col">
                <a href="index.html" class="logo" style="margin-bottom: var(--space-md);">Apogei 94</a>
                <p style="color: var(--color-text-secondary); font-size: var(--font-size-body-sm);">
                    Association reconnue d'utilité publique.<br>
                    Accompagner chaque parcours de vie.
                </p>
            </div>
            <div class="footer-col">
                <h4>Explorer</h4>
                <ul>
                    <li><a href="index.html">Nos missions</a></li>
                    <li><a href="etablissements.html">Établissements</a></li>
                    <li><a href="gouvernance.html">Gouvernance</a></li>
                    <li><a href="rapports.html">Rapports</a></li>
                </ul>
            </div>
            <div class="footer-col">
                <h4>Agir</h4>
                <ul>
                    <li><a href="don.html">Faire un don</a></li>
                    <li><a href="benevolat.html">Devenir bénévole</a></li>
                    <li><a href="carrieres.html">Recrutement</a></li>
                    <li><a href="contact.html">Contact</a></li>
                </ul>
            </div>
            <div class="footer-col">
                <h4>Légal</h4>
                <ul>
                    <li><a href="mentions-legales.html">Mentions légales</a></li>
                    <li><a href="confidentialite.html">Politique de confidentialité</a></li>
                    <li><a href="accessibilite.html">Accessibilité</a></li>
                    <li><a href="cookies.html">Cookies</a></li>
                </ul>
            </div>
        </div>
        <div class="container" style="margin-top: var(--space-2xl); padding-top: var(--space-lg); border-top: 1px solid var(--color-border-default); text-align: center; color: var(--color-text-muted); font-size: var(--font-size-body-sm);">
            &copy; 2025 Apogei 94. Tous droits réservés. Design System v2.0.
        </div>
    </footer>

    <!-- Simple JavaScript for Interactions -->
    <script>
        // Map Toggle
        const mapToggle = document.getElementById('mapToggle');
        const mapView = document.getElementById('map');
        const gridView = document.getElementById('etablissements');
        const viewButtons = document.querySelectorAll('.view-toggle-btn');

        mapToggle.addEventListener('click', () => {
            const isActive = mapToggle.classList.contains('active');

            viewButtons.forEach(btn => {
                btn.classList.toggle('active');
                btn.setAttribute('aria-pressed', btn.classList.contains('active'));
            });

            if (isActive) {
                mapView.style.display = 'none';
                gridView.closest('.establishments-section').style.display = 'block';
            } else {
                mapView.style.display = 'block';
                gridView.closest('.establishments-section').style.display = 'none';
            }
        });

        // Filter Chip Removal
        document.querySelectorAll('.filter-chip .remove').forEach(removeBtn => {
            removeBtn.addEventListener('click', (e) => {
                e.stopPropagation();
                removeBtn.closest('.filter-chip').remove();
            });
        });

        // Pagination Active State
        document.querySelectorAll('.pagination-btn:not([disabled])').forEach(btn => {
            btn.addEventListener('click', () => {
                document.querySelectorAll('.pagination-btn').forEach(b => b.classList.remove('active'));
                btn.classList.add('active');
            });
        });
    </script>
</body>
</html>
