                itemsPerPage: 6,
                view: 'grid', // 'grid' or 'map'
                sortBy: 'relevance'
            };

            // ----------------------------------------------------------------------
            // DOM ELEMENTS
            // ----------------------------------------------------------------------
            const elements = {
                // Filters
                typeTabs: document.querySelectorAll('.type-toggle-btn'),
                searchInput: document.getElementById('search'),
                poleSelect: document.getElementById('pole'),
                locationSelect: document.getElementById('location'),
                ageSelect: document.getElementById('age'),
                viewButtons: document.querySelectorAll('.view-toggle-btn'),
