            --color-border-focus: #00619b;
            --color-border-brand: #00619b;
            --color-border-brand-subtle: #99c3df;

            --color-interactive: #00619b;
            --color-interactive-hover: #00517f;
            --color-interactive-active: #004063;

            /* Typography - From tokens.json semantic.font */
            --font-family: 'Aptos', -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
            --font-size-caption-sm: 0.625rem;
            --font-size-caption: 0.75rem;
            --font-size-body-sm: 0.875rem;
            --font-size-body: 1rem;
            --font-size-body-lg: 1.125rem;
            --font-size-body-md: 1.125rem;
            --font-size-button-xl: 1.5rem;
            --font-size-h6: 0.875rem;
            --font-size-h5: 1rem;
            --font-size-h4: 1.25rem;
            --font-size-h3: 1.5rem;
            --font-size-h2: 2rem;
            --font-size-h1: 2.5rem;
            --font-size-display-sm: 3rem;
            --font-size-display: 3.75rem;
            --font-size-display-lg: 4.5rem;

            --font-weight-regular: 400;
            --font-weight-medium: 500;
            --font-weight-semibold: 600;
            --font-weight-bold: 700;

            --line-height-body: 1.55;
            --line-height-body-tight: 1.4;
            --line-height-heading: 1.2;
            --line-height-display: 1.15;

            --letter-spacing-normal: 0;
            --letter-spacing-tight: -0.025em;
            --letter-spacing-tighter: -0.05em;
            --letter-spacing-wide: 0.025em;

            /* Spacing - From tokens.json semantic.space */
            --space-none: 0;
            --space-3xs: 2px;
            --space-2xs: 4px;
            --space-xs: 8px;
            --space-sm: 12px;
            --space-md: 16px;
            --space-lg: 24px;
            --space-xl: 32px;
            --space-2xl: 48px;
            --space-3xl: 64px;
            --space-4xl: 96px;
            --space-5xl: 128px;
            --space-6xl: 192px;

            /* Radius - From tokens.json semantic.radius */
            --radius-none: 0;
            --radius-sm: 4px;
            --radius-md: 6px;
            --radius-lg: 8px;
            --radius-lg-plus: 10px;
            --radius-xl: 16px;
            --radius-full: 9999px;

            /* Shadows - From tokens.json semantic.shadow */
            --shadow-none: none;
            --shadow-sm: 0 1px 3px 0 rgba(0, 0, 0, 0.1), 0 1px 2px -1px rgba(0, 0, 0, 0.1);
            --shadow-md: 0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -2px rgba(0, 0, 0, 0.1);
            --shadow-lg: 0 10px 15px -3px rgba(0, 0, 0, 0.1), 0 4px 6px -4px rgba(0, 0, 0, 0.1);
            --shadow-xl: 0 20px 25px -5px rgba(0, 0, 0, 0.1), 0 8px 10px -6px rgba(0, 0, 0, 0.1);
            --shadow-focus: 0 0 0 3px rgba(0, 97, 155, 0.4);
            --shadow-focus-error: 0 0 0 3px rgba(239, 68, 68, 0.4);

            /* Duration & Easing */
            --duration-instant: 0ms;
            --duration-fast: 100ms;
            --duration-normal: 150ms;
            --duration-slow: 250ms;
            --duration-slower: 350ms;
            --easing-default: cubic-bezier(0.4, 0, 0.2, 1);
            --easing-enter: cubic-bezier(0, 0, 0.2, 1);
            --easing-exit: cubic-bezier(0.4, 0, 1, 1);

            /* Layout */
            --container-content: 620px;
            --container-wide: 1280px;

            /* Z-index */
            --z-base: 0;
            --z-dropdown: 1000;
            --z-sticky: 1100;
            --z-overlay: 1300;
            --z-modal: 1400;
            --z-popover: 1500;
            --z-tooltip: 1600;
        }

        /* -------------------------------------------------------------------------- */
        /* GLOBAL RESET & BASE STYLES                                                 */
        /* -------------------------------------------------------------------------- */
        *, *::before, *::after {
            box-sizing: border-box;
            margin: 0;
            padding: 0;
        }

        html {
            scroll-behavior: smooth;
        }

        body {
            font-family: var(--font-family);
            background-color: var(--color-bg-default);
            color: var(--color-text-default);
            line-height: var(--line-height-body);
            font-size: var(--font-size-body);
            font-weight: var(--font-weight-regular);
            -webkit-font-smoothing: antialiased;
            -moz-osx-font-smoothing: grayscale;
        }

        h1, h2, h3, h4, h5, h6 {
            font-family: var(--font-family);
            font-weight: var(--font-weight-semibold);
            line-height: var(--line-height-heading);
            color: var(--color-text-default);
            letter-spacing: var(--letter-spacing-tight);
        }

        h1 { font-size: var(--font-size-h1); }
        h2 { font-size: var(--font-size-h2); }
        h3 { font-size: var(--font-size-h3); }
        h4 { font-size: var(--font-size-h4); }

        a {
            color: var(--color-interactive);
            text-decoration: none;
            transition: color var(--duration-normal) var(--easing-default);
        }

        a:hover {
            color: var(--color-interactive-hover);
            text-decoration: none;
        }

        a:focus-visible,
        button:focus-visible,
        input:focus-visible,
        select:focus-visible {
            outline: 2px solid var(--color-border-focus);
            outline-offset: 2px;
            box-shadow: var(--shadow-focus);
        }

        img {
            max-width: 100%;
            height: auto;
            display: block;
            border-radius: var(--radius-lg);
        }

        /* Screen reader only */
        .sr-only {
            position: absolute;
            width: 1px;
            height: 1px;
            padding: 0;
            margin: -1px;
            overflow: hidden;
            clip: rect(0, 0, 0, 0);
            white-space: nowrap;
            border: 0;
        }

        /* Utility Classes */
        .container {
            width: 100%;
            max-width: var(--container-wide);
            margin: 0 auto;
            padding: 0 var(--space-lg);
        }

        .section-padding {
            padding: var(--space-3xl) 0;
        }

        .text-center { text-align: center; }
        .text-brand { color: var(--color-text-brand); }
        .bg-surface { background-color: var(--color-bg-surface); }
        .bg-brand { background-color: var(--color-bg-brand); }
        .text-inverse { color: var(--color-text-inverse); }
        .flex { display: flex; }
        .flex-col { flex-direction: column; }
        .items-center { align-items: center; }
        .justify-between { justify-content: space-between; }
        .gap-sm { gap: var(--space-sm); }
        .gap-md { gap: var(--space-md); }
        .gap-lg { gap: var(--space-lg); }
        .gap-xl { gap: var(--space-xl); }
        .hidden { display: none !important; }
        .visible { display: block !important; }

        /* -------------------------------------------------------------------------- */
        /* COMPONENTS                                                                 */
        /* -------------------------------------------------------------------------- */

        /* Buttons (From component.button tokens) */
        .btn {
            display: inline-flex;
            align-items: center;
            justify-content: center;
            gap: var(--space-xs);
            padding: var(--space-xs) var(--space-sm);
            font-family: var(--font-family);
            font-size: var(--font-size-body-sm);
            font-weight: var(--font-weight-semibold);
            line-height: var(--line-height-body-tight);
            border-radius: var(--radius-md);
            border: 1px solid transparent;
            cursor: pointer;
            transition: all var(--duration-normal) var(--easing-default);
            min-height: 44px;
            min-width: 44px;
            text-decoration: none;
            white-space: nowrap;
        }

        .btn-primary {
            background-color: var(--color-bg-brand);
            color: var(--color-text-inverse);
            border-color: var(--color-bg-brand);
        }

        .btn-primary:hover {
            background-color: var(--color-interactive-hover);
            border-color: var(--color-interactive-hover);
            transform: translateY(-2px);
            box-shadow: var(--shadow-md);
        }

        .btn-primary:active {
            background-color: var(--color-interactive-active);
            border-color: var(--color-interactive-active);
            transform: translateY(0);
        }

        .btn-outline {
            background-color: transparent;
            color: var(--color-text-brand);
            border-color: var(--color-border-brand);
        }

        .btn-outline:hover {
            background-color: var(--color-bg-brand-subtle);
            transform: translateY(-2px);
        }

        .btn-ghost {
            background-color: transparent;
            color: var(--color-text-default);
            border-color: transparent;
        }

        .btn-ghost:hover {
            background-color: var(--color-bg-subtle);
            color: var(--color-text-brand);
        }

        .btn-lg {
            padding: var(--space-sm) var(--space-md);
            font-size: var(--font-size-body-lg);
        }

        .btn-sm {
            padding: var(--space-2xs) var(--space-xs);
            font-size: var(--font-size-body-sm);
        }

        .btn:disabled {
            opacity: 0.6;
            cursor: not-allowed;
            transform: none !important;
        }

        /* Header */
        .header {
            background-color: var(--color-bg-surface);
            border-bottom: 1px solid var(--color-border-default);
            position: sticky;
            top: 0;
            z-index: var(--z-sticky);
            height: 80px;
            display: flex;
            align-items: center;
        }

        .header-content {
            display: flex;
            justify-content: space-between;
            align-items: center;
            width: 100%;
        }

        .logo {
            font-size: 1.5rem;
            font-weight: var(--font-weight-bold);
            color: var(--color-text-brand);
            display: flex;
            align-items: center;
            gap: var(--space-xs);
            text-decoration: none;
        }

        .logo:hover {
            color: var(--color-text-brand);
        }

        .nav-links {
            display: flex;
            gap: var(--space-xl);
            list-style: none;
        }

        .nav-links a {
            font-weight: var(--font-weight-semibold);
            color: var(--color-text-default);
            padding: var(--space-xs) 0;
            position: relative;
        }

        .nav-links a:hover,
        .nav-links a.active {
            color: var(--color-text-brand);
        }

        .nav-links a.active::after {
            content: '';
            position: absolute;
            bottom: -1px;
            left: 0;
            right: 0;
            height: 2px;
            background-color: var(--color-border-brand);
            border-radius: var(--radius-full);
        }

        .header-actions {
            display: flex;
            gap: var(--space-sm);
        }

        /* Breadcrumb */
        .breadcrumb {
            padding: var(--space-lg) 0;
            background-color: var(--color-bg-surface);
            border-bottom: 1px solid var(--color-border-subtle);
        }

        .breadcrumb-list {
            display: flex;
            align-items: center;
            gap: var(--space-sm);
            list-style: none;
            font-size: var(--font-size-body-sm);
            color: var(--color-text-secondary);
            flex-wrap: wrap;
        }

        .breadcrumb-list li {
            display: flex;
            align-items: center;
            gap: var(--space-sm);
        }

        .breadcrumb-list a {
            color: var(--color-text-brand);
        }

        .breadcrumb-list a:hover {
            text-decoration: underline;
        }

        /* Page Hero */
        .detail-hero {
            background-color: var(--color-bg-surface);
            padding: var(--space-3xl) 0;
            border-bottom: 1px solid var(--color-border-default);
        }

        .detail-hero-grid {
            display: grid;
            grid-template-columns: 1fr 1fr;
            gap: var(--space-3xl);
            align-items: start;
        }

        .detail-hero-content {
            padding-top: var(--space-md);
        }

        .detail-hero-badges {
            display: flex;
            flex-wrap: wrap;
            gap: var(--space-xs);
            margin-bottom: var(--space-md);
        }

        .badge {
            padding: var(--space-2xs) var(--space-sm);
            border-radius: var(--radius-full);
            font-size: var(--font-size-caption);
            font-weight: var(--font-weight-semibold);
            display: inline-flex;
            align-items: center;
            gap: var(--space-2xs);
        }

        .badge-pole {
            background-color: var(--color-bg-brand);
            color: var(--color-text-inverse);
        }

        .badge-type {
            background-color: var(--color-bg-accent-green);
            color: var(--color-text-inverse);
        }

        .badge-status {
            background-color: var(--color-bg-success-subtle);
            color: var(--color-text-success);
        }

        .detail-hero-title {
            font-size: var(--font-size-h1);
            margin-bottom: var(--space-md);
            line-height: var(--line-height-display);
            letter-spacing: var(--letter-spacing-tighter);
        }

        .detail-hero-location {
            display: flex;
            align-items: center;
            gap: var(--space-xs);
            font-size: var(--font-size-body-lg);
            color: var(--color-text-secondary);
            margin-bottom: var(--space-lg);
        }

        .detail-hero-description {
            font-size: var(--font-size-body-lg);
            color: var(--color-text-secondary);
            margin-bottom: var(--space-xl);
            line-height: var(--line-height-body);
        }

        .detail-hero-actions {
            display: flex;
            gap: var(--space-md);
            flex-wrap: wrap;
        }

        .detail-hero-media {
            position: relative;
            border-radius: var(--radius-xl);
            overflow: hidden;
            box-shadow: var(--shadow-lg);
            aspect-ratio: 4/3;
        }

        .detail-hero-media img {
            width: 100%;
            height: 100%;
            object-fit: cover;
            border-radius: var(--radius-xl);
        }

        .media-gallery-trigger {
            position: absolute;
            bottom: var(--space-md);
            right: var(--space-md);
            background-color: var(--color-bg-surface);
            padding: var(--space-xs) var(--space-sm);
            border-radius: var(--radius-md);
            font-size: var(--font-size-body-sm);
            font-weight: var(--font-weight-semibold);
            display: flex;
            align-items: center;
            gap: var(--space-2xs);
            cursor: pointer;
            box-shadow: var(--shadow-md);
        }

        /* Info Cards Grid */
        .info-section {
            padding: var(--space-3xl) 0;
        }

        .info-grid {
            display: grid;
            grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
            gap: var(--space-lg);
            margin-bottom: var(--space-3xl);
        }

        .info-card {
            background-color: var(--color-bg-surface);
            border: 1px solid var(--color-border-default);
            border-radius: var(--radius-xl);
            padding: var(--space-lg);
            transition: all var(--duration-slow) var(--easing-default);
        }

        .info-card:hover {
            border-color: var(--color-border-brand);
            box-shadow: var(--shadow-md);
        }

        .info-card-icon {
            width: 48px;
            height: 48px;
            background-color: var(--color-bg-brand-subtle);
            border-radius: var(--radius-lg);
            display: flex;
            align-items: center;
            justify-content: center;
            margin-bottom: var(--space-md);
            color: var(--color-text-brand);
        }

        .info-card-title {
            font-size: var(--font-size-h4);
            margin-bottom: var(--space-sm);
        }

        .info-card-content {
            font-size: var(--font-size-body-sm);
            color: var(--color-text-secondary);
            line-height: var(--line-height-body);
        }

        .info-card-list {
            list-style: none;
            margin-top: var(--space-sm);
        }

        .info-card-list li {
            display: flex;
            align-items: center;
            gap: var(--space-xs);
            padding: var(--space-2xs) 0;
            font-size: var(--font-size-body-sm);
            color: var(--color-text-secondary);
        }

        .info-card-list li svg {
            flex-shrink: 0;
            color: var(--color-text-brand);
        }

        /* Content Sections */
        .content-section {
            background-color: var(--color-bg-surface);
            border: 1px solid var(--color-border-default);
            border-radius: var(--radius-xl);
            padding: var(--space-xl);
            margin-bottom: var(--space-lg);
        }

        .content-section-title {
            font-size: var(--font-size-h2);
            margin-bottom: var(--space-lg);
            padding-bottom: var(--space-md);
            border-bottom: 1px solid var(--color-border-subtle);
        }

        .content-section-text {
            font-size: var(--font-size-body);
            color: var(--color-text-secondary);
            line-height: var(--line-height-body);
            margin-bottom: var(--space-md);
        }

        .content-section-text:last-child {
            margin-bottom: 0;
        }

        /* Services Grid */
        .services-grid {
            display: grid;
            grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
            gap: var(--space-lg);
            margin-top: var(--space-lg);
        }

        .service-card {
            background-color: var(--color-bg-subtle);
            border-radius: var(--radius-lg);
            padding: var(--space-lg);
            transition: all var(--duration-normal) var(--easing-default);
        }

        .service-card:hover {
            background-color: var(--color-bg-brand-subtle);
        }

        .service-card-icon {
            width: 40px;
            height: 40px;
            background-color: var(--color-bg-brand);
            border-radius: var(--radius-md);
            display: flex;
            align-items: center;
            justify-content: center;
            margin-bottom: var(--space-md);
            color: var(--color-text-inverse);
        }

        .service-card-title {
            font-size: var(--font-size-h4);
            margin-bottom: var(--space-sm);
            color: var(--color-text-default);
        }

        .service-card-description {
            font-size: var(--font-size-body-sm);
            color: var(--color-text-secondary);
            line-height: var(--line-height-body);
        }

        /* Team Section */
        .team-grid {
            display: grid;
            grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
            gap: var(--space-lg);
            margin-top: var(--space-lg);
        }

        .team-card {
            text-align: center;
            padding: var(--space-lg);
            background-color: var(--color-bg-subtle);
            border-radius: var(--radius-lg);
        }

        .team-avatar {
            width: 80px;
            height: 80px;
            border-radius: 50%;
            background-color: var(--color-bg-muted);
            margin: 0 auto var(--space-md);
            object-fit: cover;
        }

        .team-name {
            font-size: var(--font-size-h4);
            margin-bottom: var(--space-2xs);
        }

        .team-role {
            font-size: var(--font-size-body-sm);
            color: var(--color-text-secondary);
            margin-bottom: var(--space-xs);
        }

        .team-contact {
            font-size: var(--font-size-body-sm);
            color: var(--color-text-brand);
        }

        /* Map Section */
        .map-section {
            background-color: var(--color-bg-surface);
            border: 1px solid var(--color-border-default);
            border-radius: var(--radius-xl);
            overflow: hidden;
            margin-bottom: var(--space-lg);
        }

        .map-container {
            height: 400px;
            background: linear-gradient(135deg, var(--color-bg-brand-subtle) 0%, var(--color-bg-accent-cyan-subtle) 100%);
            display: flex;
            align-items: center;
            justify-content: center;
            color: var(--color-text-brand);
            font-size: var(--font-size-body-lg);
            flex-direction: column;
            gap: var(--space-md);
            text-align: center;
            padding: var(--space-lg);
        }

        .map-address {
            padding: var(--space-lg);
            background-color: var(--color-bg-subtle);
            display: flex;
            align-items: center;
            gap: var(--space-md);
            font-size: var(--font-size-body);
            color: var(--color-text-secondary);
        }

        /* Contact Form */
        .contact-form {
            display: grid;
            grid-template-columns: 1fr 1fr;
            gap: var(--space-md);
            margin-top: var(--space-lg);
        }

        .form-group {
            display: flex;
            flex-direction: column;
            gap: var(--space-2xs);
        }

        .form-group.full-width {
            grid-column: 1 / -1;
        }

        .form-label {
            font-size: var(--font-size-body-sm);
            font-weight: var(--font-weight-semibold);
            color: var(--color-text-default);
        }

        .form-input,
        .form-textarea,
        .form-select {
            padding: var(--space-xs) var(--space-sm);
            border: 1px solid var(--color-border-strong);
            border-radius: var(--radius-md);
            font-size: var(--font-size-body-sm);
            font-family: var(--font-family);
            min-height: 44px;
            background-color: var(--color-bg-surface);
            transition: border-color var(--duration-normal) var(--easing-default);
        }

        .form-input:focus,
        .form-textarea:focus,
        .form-select:focus {
            border-color: var(--color-border-focus);
            outline: none;
            box-shadow: var(--shadow-focus);
        }

        .form-textarea {
            min-height: 120px;
            resize: vertical;
        }

        .form-help {
            font-size: var(--font-size-caption);
            color: var(--color-text-muted);
        }

        /* Related Establishments */
        .related-grid {
            display: grid;
            grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
            gap: var(--space-lg);
            margin-top: var(--space-lg);
        }

        .related-card {
            background-color: var(--color-bg-surface);
            border: 1px solid var(--color-border-default);
            border-radius: var(--radius-lg);
            overflow: hidden;
            transition: all var(--duration-slow) var(--easing-default);
        }

        .related-card:hover {
            transform: translateY(-4px);
            box-shadow: var(--shadow-md);
            border-color: var(--color-border-brand);
        }

        .related-media {
            height: 160px;
            background-color: var(--color-bg-muted);
            overflow: hidden;
        }

        .related-media img {
            width: 100%;
            height: 100%;
            object-fit: cover;
            border-radius: 0;
        }

        .related-content {
            padding: var(--space-md);
        }

        .related-location {
            display: flex;
            align-items: center;
            gap: var(--space-2xs);
            font-size: var(--font-size-body-sm);
            color: var(--color-text-secondary);
            margin-bottom: var(--space-2xs);
        }

        .related-title {
            font-size: var(--font-size-h4);
            margin-bottom: var(--space-xs);
        }

        .related-title a {
            color: var(--color-text-default);
        }

        .related-title a:hover {
            color: var(--color-text-brand);
        }

        .related-type {
            display: inline-block;
            padding: var(--space-2xs) var(--space-xs);
            background-color: var(--color-bg-brand-subtle);
            color: var(--color-text-brand);
            font-size: var(--font-size-caption);
            font-weight: var(--font-weight-semibold);
            border-radius: var(--radius-full);
            margin-bottom: var(--space-sm);
