        document.addEventListener('DOMContentLoaded', () => {
            // Form validation feedback
            const form = document.querySelector('.contact-form');
            if (form) {
                form.addEventListener('submit', (e) => {
                    e.preventDefault();

                    // Simple validation feedback (in production, use proper validation)
                    const requiredFields = form.querySelectorAll('[required]');
                    let isValid = true;

                    requiredFields.forEach(field => {
                        if (!field.value.trim()) {
                            isValid = false;
                            field.style.borderColor = 'var(--color-border-error)';
                            field.style.boxShadow = 'var(--shadow-focus-error)';
                        } else {
                            field.style.borderColor = 'var(--color-border-strong)';
                            field.style.boxShadow = 'none';
                        }
                    });

                    if (isValid) {
                        // Show success message (in production, submit to server)
                        alert('✅ Votre demande a été envoyée avec succès ! Nous vous répondrons sous 48h.');
                        form.reset();
                    } else {
                        alert('⚠️ Veuillez remplir tous les champs obligatoires.');
                    }
                });
            }

            // Gallery trigger (placeholder for modal)
            const galleryTrigger = document.querySelector('.media-gallery-trigger');
            if (galleryTrigger) {
                galleryTrigger.addEventListener('click', () => {
                    alert('🖼️ Galerie photo - Fonctionnalité à implémenter avec lightbox/modal');
                });
            }

            // Smooth scroll for anchor links
            document.querySelectorAll('a[href^="#"]').forEach(anchor => {
                anchor.addEventListener('click', function (e) {
                    e.preventDefault();
                    const target = document.querySelector(this.getAttribute('href'));
                    if (target) {
                        target.scrollIntoView({
                            behavior: 'smooth',
                            block: 'start'
                        });
                    }
                });
            });

            console.log('✅ Établissement Detail Page initialized');
            console.log('📊 Design tokens loaded from tokens.json & theme.json');
            console.log('♿ Accessibility features enabled');
        });
