                },
                activeChips: [],
                sortBy: 'relevance'
            };

            // ----------------------------------------------------------------------
            // DOM ELEMENTS
            // ----------------------------------------------------------------------
            const elements = {
                // Filters
                searchInput: document.getElementById('job-search'),
                typeSelect: document.getElementById('job-type'),
                locationSelect: document.getElementById('job-location'),
                metierSelect: document.getElementById('job-metier'),
                clearFiltersBtn: document.getElementById('clear-filters'),
                activeFiltersContainer: document.getElementById('active-filters'),

                // Results
                jobsGrid: document.getElementById('jobs-grid'),
                jobsCount: document.getElementById('jobs-count'),
                emptyState: document.getElementById('empty-state'),
                loading: document.getElementById('loading'),

                // Empty state reset
                resetFromEmpty: document.getElementById('reset-from-empty')
            };

            // ----------------------------------------------------------------------
            // UTILITY FUNCTIONS
            // ----------------------------------------------------------------------

            // Debounce function for search input
            function debounce(func, wait) {
                let timeout;
                return function executedFunction(...args) {
                    const later = () => {
                        clearTimeout(timeout);
                        func(...args);
                    };
                    clearTimeout(timeout);
                    timeout = setTimeout(later, wait);
                };
            }

            // Escape HTML to prevent XSS
            function escapeHtml(text) {
                const div = document.createElement('div');
                div.textContent = text;
                return div.innerHTML;
            }

            // Format type label for display
            function formatTypeLabel(type) {
                const labels = {
                    'cdi': 'CDI',
                    'cdd': 'CDD',
                    'interim': 'Intérim',
                    'stage': 'Stage'
                };
                return labels[type] || type;
            }

            // Format location label for display
            function formatLocationLabel(location) {
                const labels = {
                    'creteil': 'Créteil',
                    'saint-maur': 'Saint-Maur',
                    'vitry': 'Vitry-sur-Seine',
                    'champigny': 'Champigny-sur-Marne',
                    'ivry': 'Ivry-sur-Seine',
                    'bonneuil': 'Bonneuil-sur-Marne'
                };
                return labels[location] || location;
            }

            // Format metier label for display
            function formatMetierLabel(metier) {
                const labels = {
                    'educateur': 'Éducateur',
                    'aide-soignant': 'Aide-soignant',
                    'infirmier': 'Infirmier',
                    'moniteur': 'Moniteur',
                    'psychologue': 'Psychologue',
                    'directeur': 'Directeur'
                };
                return labels[metier] || metier;
            }

            // ----------------------------------------------------------------------
            // FILTERING LOGIC
            // ----------------------------------------------------------------------

            function applyFilters() {
                // Show loading state
                elements.jobsGrid.innerHTML = '';
                elements.loading.classList.remove('hidden');
                elements.jobsGrid.classList.add('hidden');
                elements.emptyState.classList.add('hidden');

                // Simulate API delay for realistic UX
                setTimeout(() => {
                    let filtered = [...jobsData];

                    // Apply search filter
                    if (state.filters.search) {
                        const searchLower = state.filters.search.toLowerCase();
                        filtered = filtered.filter(job => 
                            job.title.toLowerCase().includes(searchLower) ||
                            job.description.toLowerCase().includes(searchLower) ||
                            job.tags.some(tag => tag.toLowerCase().includes(searchLower))
                        );
                    }

                    // Apply type filter
                    if (state.filters.type) {
                        filtered = filtered.filter(job => job.type === state.filters.type);
                    }

                    // Apply location filter
                    if (state.filters.location) {
                        filtered = filtered.filter(job => job.locationSlug === state.filters.location);
                    }

                    // Apply metier filter
                    if (state.filters.metier) {
                        filtered = filtered.filter(job => job.metier === state.filters.metier);
                    }

                    // Update UI
                    renderJobs(filtered);
                    updateJobsCount(filtered.length);
                    updateActiveChips();
                    toggleClearFiltersButton();

                    // Hide loading
                    elements.loading.classList.add('hidden');
                }, 300);
            }

            function renderJobs(jobs) {
                if (jobs.length === 0) {
                    elements.jobsGrid.classList.add('hidden');
                    elements.emptyState.classList.remove('hidden');
                    return;
                }

                elements.emptyState.classList.add('hidden');
                elements.jobsGrid.classList.remove('hidden');

                // Render job cards
                elements.jobsGrid.innerHTML = jobs.map(job => createJobCard(job)).join('');
            }

            function createJobCard(job) {
                return `
                    <article class="job-card" role="listitem" tabindex="0" aria-labelledby="job-title-${job.id}">
                        <div class="job-header">
                            <div>
                                <h3 class="job-title">
                                    <a href="offre-detail.html?id=${job.id}" id="job-title-${job.id}">
                                        ${escapeHtml(job.title)}
                                    </a>
                                </h3>
                                <div class="job-meta">
                                    <span class="meta-item">
                                        <svg width="16" height="16" viewBox="0 0 16 16" fill="none" xmlns="http://www.w3.org/2000/svg" aria-hidden="true">
