        .related-jobs {
            display: grid; grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
            gap: var(--space-lg); margin-top: var(--space-lg);
        }

        .related-job-card {
            background-color: var(--color-bg-surface); border: 1px solid var(--color-border-default);
            border-radius: var(--radius-lg); padding: var(--space-md);
            transition: all var(--duration-slow) var(--easing-default);
        }

        .related-job-card:hover {
            border-color: var(--color-border-brand); box-shadow: var(--shadow-md);
            transform: translateY(-2px);
        }

        .related-job-title {
            font-size: var(--font-size-h4); margin-bottom: var(--space-xs);
        }

        .related-job-title a { color: var(--color-text-default); }
        .related-job-title a:hover { color: var(--color-text-brand); }

        .related-job-meta {
            font-size: var(--font-size-body-sm); color: var(--color-text-secondary);
            margin-bottom: var(--space-sm);
        }

        .related-job-tags {
            display: flex; gap: var(--space-xs); flex-wrap: wrap;
        }

        .job-tag {
            padding: var(--space-2xs) var(--space-xs); background-color: var(--color-bg-subtle);
            color: var(--color-text-secondary); font-size: var(--font-size-caption);
            border-radius: var(--radius-md); font-weight: var(--font-weight-medium);
        }

        /* Footer */
