    <script>
        // Impact Calculator
        const amountBtns = document.querySelectorAll('.amount-btn');
        const customAmountInput = document.getElementById('custom-amount');
        const frequencyBtns = document.querySelectorAll('.frequency-btn');
        const displayAmount = document.getElementById('display-amount');
        const buttonAmount = document.getElementById('button-amount');
        const impact1 = document.getElementById('impact-1');
        const impact2 = document.getElementById('impact-2');
        const impact3 = document.getElementById('impact-3');

        let currentAmount = 50;
        let currentFrequency = 'one-time';

        // Impact calculations (example ratios)
        function calculateImpact(amount) {
            const meals = Math.round(amount / 3.33); // 1 repas = ~3.33€
            const hours = Math.round(amount / 10); // 1h accompagnement = ~10€
            const workshops = Math.round(amount / 16.67); // 1 atelier = ~16.67€

            impact1.textContent = `${meals} repas adaptés`;
            impact2.textContent = `${hours}h d'accompagnement`;
            impact3.textContent = `${workshops} ateliers thérapeutiques`;
        }

        function updateDisplay() {
            displayAmount.textContent = currentAmount;
            buttonAmount.textContent = currentAmount;
            calculateImpact(currentAmount);

            // Update button states
            amountBtns.forEach(btn => {
                btn.classList.toggle('active', parseInt(btn.dataset.amount) === currentAmount);
            });

            if (customAmountInput.value && parseInt(customAmountInput.value) !== currentAmount) {
                amountBtns.forEach(btn => btn.classList.remove('active'));
            }
        }

        // Amount button clicks
        amountBtns.forEach(btn => {
            btn.addEventListener('click', () => {
                currentAmount = parseInt(btn.dataset.amount);
                customAmountInput.value = '';
                updateDisplay();
            });
        });

        // Custom amount input
        customAmountInput.addEventListener('input', (e) => {
            const value = parseInt(e.target.value);
            if (value && value >= 1 && value <= 10000) {
                currentAmount = value;
                updateDisplay();
            }
        });

        // Frequency toggle
        frequencyBtns.forEach(btn => {
            btn.addEventListener('click', () => {
                currentFrequency = btn.dataset.frequency;
                frequencyBtns.forEach(b => b.classList.remove('active'));
                btn.classList.add('active');
            });
        });

        // Form submission
        document.getElementById('donate-form').addEventListener('submit', function(e) {
            e.preventDefault();

            // In production, redirect to payment gateway
            alert(`✅ Redirection vers le paiement sécurisé\n\nMontant: ${currentAmount}€\nFréquence: ${currentFrequency === 'one-time' ? 'Unique' : 'Mensuel'}\n\nIntégration HelloAsso/Stripe à implémenter`);
        });

        // Initialize
        updateDisplay();
        console.log('✅ Donation Page initialized');
        console.log('💰 Impact Calculator active');
    </script>
</body>
</html>
